// src/pages/SupplierRequestDetails.jsx
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Container, Card, Button, Form, Alert, Spinner } from "react-bootstrap";
import io from "socket.io-client";

const BACKEND = "http://localhost:5000";
const socket = io(BACKEND, { autoConnect: true });

export default function SupplierRequestDetails() {
  const { requestId } = useParams();
  const [request, setRequest] = useState(null);
  const [enteredOtp, setEnteredOtp] = useState("");
  const [verificationMsg, setVerificationMsg] = useState("");
  const [loading, setLoading] = useState(false);
  const [paymentMode, setPaymentMode] = useState("");
  const [paymentConfirmed, setPaymentConfirmed] = useState(false);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchRequest = async () => {
      try {
        const res = await axios.get(
          `${BACKEND}/api/request/status/${requestId}`,
          { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
        );
        setRequest(res.data.request);
      } catch (err) {
        console.error("Error fetching request details:", err);
      }
    };
    fetchRequest();

    // Join the same room
    socket.emit("joinRoom", { room: requestId });
    console.log("ðŸ“¡ Supplier joined room:", requestId);

    // Listen for customer payment events
    socket.on("cashPaymentChosen", (data) => {
      console.log("ðŸ’µ Customer selected Cash on Delivery:", data);
      if (data.requestId === requestId) {
        setPaymentMode("CASH");
        setMessage("Customer has chosen Cash on Delivery. Please collect payment.");
      }
    });

    socket.on("upiPaymentDone", (data) => {
      console.log("ðŸ’³ UPI Payment Done:", data);
      if (data.requestId === requestId) {
        setPaymentMode("UPI");
        setMessage("Customer completed UPI payment. Redirecting â€¦");
      }
    });

    return () => {
      socket.off("cashPaymentChosen");
      socket.off("upiPaymentDone");
    };
  }, [requestId]);

  // rest of your code remains the same

  return (
    <Container className="mt-5">
      {/* UI code for OTP / payment / confirm */}
    </Container>
  );
}
