// src/pages/SupplierDashboard.jsx
import React, { useEffect, useState } from "react";
import { Container, Row, Col, Button, Alert, Modal } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import io from "socket.io-client";
import axios from "axios";
import { motion, AnimatePresence } from "framer-motion";

const BACKEND = "http://localhost:5000";
const socket = io(BACKEND, { autoConnect: true });

export default function SupplierDashboard() {
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = useState(false);

  const [requests, setRequests] = useState([]);
  const [showStats, setShowStats] = useState(false);
  const [stats, setStats] = useState(null);

  const supplierId = localStorage.getItem("supplierId");

  // ----------------- Fetch Stats -----------------
  const loadStats = async () => {
    try {
      const res = await axios.get(`${BACKEND}/api/supplier/statistics/${supplierId}`);
      setStats(res.data);
    } catch (err) {
      console.error("Stats error:", err);
    }
  };

  // ----------------- Socket Listener -----------------
  useEffect(() => {
    if (!supplierId) return;

    socket.emit("joinRoom", { room: supplierId });
    console.log("ðŸ“¡ Supplier joined room:", supplierId);

    socket.on("newChargingRequest", (data) => {
      console.log("ðŸ”‹ New request received:", data);
      setRequests((prev) => [...prev, data.request]);
    });

    return () => {
      socket.off("newChargingRequest");
    };
  }, [supplierId]);

  // ----------------- Accept Request -----------------
  const handleAccept = async (reqId) => {
    try {
      await axios.post(
        `${BACKEND}/api/request/accept/${reqId}`,
        {},
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      setRequests((prev) => prev.filter((r) => r._id !== reqId));
      navigate(`/supplier/request/${reqId}`);
    } catch (err) {
      console.error("Accept error:", err);
    }
  };

  // ----------------- Reject Request -----------------
  const handleReject = async (reqId) => {
    try {
      await axios.post(
        `${BACKEND}/api/request/reject/${reqId}`,
        {},
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      setRequests((prev) => prev.filter((r) => r._id !== reqId));
    } catch (err) {
      console.error("Reject error:", err);
    }
  };

  // ----------------- UI Render -----------------
  return (
    <Container className="mt-5">
      <Row>
        <Col>
          <h2 className={darkMode ? "text-light" : "text-dark"}>
            âš¡ Supplier Dashboard
          </h2>
          <p className={darkMode ? "text-light" : "text-dark"}>
            Monitor customer requests and manage your charging station.
          </p>
        </Col>

        <Col className="text-end">
          <Button
            variant="info"
            onClick={() => {
              loadStats();
              setShowStats(true);
            }}
          >
            ðŸ“Š View Statistics
          </Button>
        </Col>
      </Row>

      <hr className={darkMode ? "border-secondary" : ""} />

      {/* Notifications */}
      <div
        style={{
          position: "fixed",
          top: "90px",
          right: "30px",
          zIndex: 2000,
          width: "340px",
        }}
      >
        <AnimatePresence>
          {requests.map((req) => (
            <motion.div
              key={req._id}
              initial={{ opacity: 0, x: 100 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 100 }}
            >
              <Alert
                variant="info"
                className={darkMode ? "bg-dark text-light border-secondary" : ""}
              >
                <strong>ðŸ”‹ New Request</strong>
                <p>
                  From: <b>{req.customerName}</b>
                </p>
                <p>Units Needed: {req.unitsNeeded}</p>

                <div className="d-flex justify-content-end">
                  <Button size="sm" variant="success" onClick={() => handleAccept(req._id)}>
                    Accept
                  </Button>
                  <Button
                    size="sm"
                    variant="danger"
                    className="ms-2"
                    onClick={() => handleReject(req._id)}
                  >
                    Reject
                  </Button>
                </div>
              </Alert>
            </motion.div>
          ))}
        </AnimatePresence>
      </div>

      {/* Stats Modal */}
      <Modal show={showStats} onHide={() => setShowStats(false)}>
        <Modal.Header closeButton>
          <Modal.Title>ðŸ“ˆ Charging Station Statistics</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          {stats ? (
            <>
              <p>
                <b>Total Requests:</b> {stats.totalRequests}
              </p>
              <p>
                <b>Total Units Supplied:</b> {stats.totalUnitsSupplied}
              </p>
              <p>
                <b>Total Revenue:</b> â‚¹{stats.totalRevenue}
              </p>
            </>
          ) : (
            <p>Loading...</p>
          )}
        </Modal.Body>
      </Modal>
    </Container>
  );
}
