// src/pages/RequestDetails.jsx
import React, { useEffect, useState, useRef } from "react";
import io from "socket.io-client";
import axios from "axios";
import { Container, Button, Alert, Spinner } from "react-bootstrap";

const BACKEND = "http://localhost:5000";
const socket = io(BACKEND, { autoConnect: true });

function RequestDetails({ requestId: propRequestId }) {
  const [request, setRequest] = useState(null);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [paymentMode, setPaymentMode] = useState("");
  const [paymentConfirmed, setPaymentConfirmed] = useState(false);
  const mounted = useRef(false);

  // ‚úÖ Fetch request details
  const fetchRequest = async (id) => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(`${BACKEND}/api/request/status/${id}`, {
        headers: token ? { Authorization: `Bearer ${token}` } : {},
      });

      setRequest(res.data.request);
      setLoading(false);
    } catch (err) {
      console.error("‚ùå Error fetching request details:", err);
      setLoading(false);
    }
  };

  useEffect(() => {
    mounted.current = true;
    const requestId = propRequestId || localStorage.getItem("requestId");
    const supplierId =
      localStorage.getItem("supplierId") || localStorage.getItem("userId");

    if (requestId) fetchRequest(requestId);

    // ‚úÖ Join supplier socket room
    if (supplierId) {
      socket.emit("joinSupplierRoom", supplierId);
      console.log("üì° Joined supplier room:", supplierId);
    }

    // ‚úÖ Listen for payment events from customer
    socket.on("cashPaymentChosen", (data) => {
      if (!mounted.current) return;
      console.log("üíµ Customer chose COD:", data);
      setPaymentMode("CASH");
      setMessage(data.message);
    });

    socket.on("upiPaymentDone", (data) => {
      if (!mounted.current) return;
      console.log("üí≥ UPI Payment done:", data);
      setPaymentMode("UPI");
      setMessage(data.message);
      // Redirect supplier after 3 sec
      setTimeout(() => {
        window.location.href = "/supplier/dashboard";
      }, 3000);
    });

    return () => {
      mounted.current = false;
      socket.off("cashPaymentChosen");
      socket.off("upiPaymentDone");
    };
  }, [propRequestId]);

  // ‚úÖ Supplier manually confirms COD payment
  const handleConfirmPayment = async () => {
    try {
      const requestId = request._id;
      const totalAmount =
        (request?.supplier?.pricePerUnit || 0) * (request?.unitsRequested || 0);

      await axios.put(`${BACKEND}/api/request/${requestId}/markPaid`, {
        mode: "CASH_CONFIRMED",
        amountPaid: totalAmount,
        transactionId: "CASH-" + Date.now(),
      });

      setPaymentConfirmed(true);
      alert("‚úÖ Payment confirmed successfully!");
      window.location.href = "/supplier/dashboard";
    } catch (err) {
      console.error("‚ùå Error confirming payment:", err);
      alert("Failed to confirm payment.");
    }
  };

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" />
        <p>Loading booking details...</p>
      </Container>
    );
  }

  if (!request) {
    return (
      <Container className="text-center mt-5">
        <Alert variant="danger">Request not found</Alert>
      </Container>
    );
  }

  return (
    <Container className="mt-5 text-center">
      <h3>‚ö° Booking Details</h3>

      <Alert variant="info" className="mt-3">
        <b>Status:</b> {request.status}
      </Alert>

      {message && (
        <Alert
          variant={
            paymentMode === "CASH"
              ? "warning"
              : paymentMode === "UPI"
              ? "success"
              : "info"
          }
          className="mt-3"
        >
          {message}
        </Alert>
      )}

      {paymentMode === "CASH" && !paymentConfirmed && (
        <div className="mt-4">
          <Alert variant="light">
            üíµ <b>Cash on Delivery Selected</b>
            <p>Confirm once you have collected the payment.</p>
          </Alert>
          <Button variant="success" onClick={handleConfirmPayment}>
            ‚úÖ Payment Received
          </Button>
        </div>
      )}

      {paymentMode === "UPI" && (
        <div className="mt-4">
          <Alert variant="success">
            üí≥ Payment completed successfully by customer.
          </Alert>
        </div>
      )}

      {paymentConfirmed && (
        <Alert className="mt-4" variant="success">
          ‚úÖ Payment confirmed! Redirecting to dashboard...
        </Alert>
      )}
    </Container>
  );
}

export default RequestDetails;
