// src/pages/BookingPage.jsx
import React, { useEffect, useState, useRef } from "react";
import io from "socket.io-client";
import axios from "axios";
import { Spinner, Container, Button, Alert } from "react-bootstrap";

const BACKEND = "http://localhost:5000";
const socket = io(BACKEND, { autoConnect: true });

function BookingPage({ requestId: propRequestId }) {
  const [status, setStatus] = useState("PENDING");
  const [otp, setOtp] = useState("");
  const [supplier, setSupplier] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const [chargedUnits, setChargedUnits] = useState(0);
  const [supplierPrice, setSupplierPrice] = useState(0);
  const mounted = useRef(false);

  // âœ… Fetch current request details
  const fetchRequest = async (id) => {
    try {
      const token = localStorage.getItem("token");
      const url = `${BACKEND}/api/request/status/${id}`;
      const res = await axios.get(url, {
        headers: token ? { Authorization: `Bearer ${token}` } : {},
      });

      const req = res.data.request;
      if (!req) return;

      setLoading(false);
      setStatus(req.status);
      if (req.status === "ACCEPTED" || req.status === "COMPLETED") {
        setOtp(req.otp || "");
        setSupplier(req.supplier || null);
        if (req.supplier?.pricePerUnit) setSupplierPrice(req.supplier.pricePerUnit);
      }
    } catch (err) {
      console.error("âŒ Error fetching request status:", err);
      setLoading(false);
    }
  };

  useEffect(() => {
    mounted.current = true;
    const requestId = propRequestId || localStorage.getItem("requestId");
    const customerId =
      localStorage.getItem("customerId") || localStorage.getItem("userId");

    if (requestId) fetchRequest(requestId);

    if (customerId) {
      socket.emit("joinCustomerRoom", customerId);
    }

   const onChargingCompleted = (data) => {
  console.log("ðŸ”‹ chargingCompleted:", data);
  if (!mounted.current) return;

  setStatus("COMPLETED");
  setMessage(data.message || "âœ… Charging completed! Please proceed to payment.");

  // âœ… Assign received data properly
  setChargedUnits(data.chargedUnits || 0);
  if (data.supplier) {
    setSupplier(data.supplier);
  } else {
    setSupplier(null);
  }
};

    socket.on("chargingCompleted", onChargingCompleted);

    return () => {
      mounted.current = false;
      socket.off("chargingCompleted", onChargingCompleted);
    };
  }, [propRequestId]);

  // âœ… Trace supplier on Google Maps
  const handleTraceSupplier = () => {
    if (!supplier) return alert("Supplier info not available yet.");
    if (supplier.coordinates?.lat && supplier.coordinates?.lng) {
      window.open(
        `https://www.google.com/maps?q=${supplier.coordinates.lat},${supplier.coordinates.lng}`,
        "_blank"
      );
    } else if (supplier.location) {
      const encoded = encodeURIComponent(supplier.location);
      window.open(`https://www.google.com/maps?q=${encoded}`, "_blank");
    } else {
      alert("Supplier location not available.");
    }
  };

  // âœ… Payment logic (with null checks)
  const handlePayment = async (method) => {
    if (method === "CASH") {
      alert("Please pay the supplier in cash upon completion.");
      return;
    }

    if (!supplier || !supplierPrice || !chargedUnits) {
      alert("Payment details not ready. Please wait a few seconds.");
      return;
    }

    const amount = chargedUnits * supplierPrice;
    const upiId = supplier.upiId || "supplier@upi";
    const name = encodeURIComponent(supplier.name || "Supplier");

    const upiUrl = `upi://pay?pa=${upiId}&pn=${name}&am=${amount}&cu=INR`;
    console.log("ðŸ”— Redirecting to:", upiUrl);
    window.location.href = upiUrl;
  };

  // âœ… Loading view
  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" />
        <p>Waiting for supplier approval...</p>
      </Container>
    );
  }

  // âœ… Render main view
  return (
    <Container className="mt-5 text-center">
      <h3>âš¡ Booking Request</h3>

      {message && (
        <Alert
          variant={
            status === "REJECTED"
              ? "danger"
              : status === "COMPLETED"
              ? "success"
              : "info"
          }
          className="mt-3"
        >
          {message}
        </Alert>
      )}
{status === "COMPLETED" && (
  <div className="mt-4">
    <h5>Charging Completed âœ…</h5>
    <p>Total Units: {chargedUnits}</p>
    <p>
      Amount to Pay: â‚¹
      {(chargedUnits * (supplier?.pricePerUnit || 0)).toFixed(2)}
    </p>

    <div className="d-flex justify-content-center gap-3 mt-3">
      <Button
        variant="success"
        onClick={() => handlePayment("UPI")}
      >
        ðŸ’³ Pay via UPI
      </Button>
      <Button
        variant="warning"
        onClick={() => handlePayment("CASH")}
      >
        ðŸ’µ Pay by Cash
      </Button>
    </div>
  </div>
)}

    </Container>
  );
}

export default BookingPage;
