import React, { useEffect, useState } from "react";
import axios from "axios";
import { Container, Table } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { Card, Button, Row, Col } from "react-bootstrap";

function DashboardCustomer() {
  const [suppliers, setSuppliers] = useState([]);

  useEffect(() => {
  const fetchSuppliers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/customer/dashboard", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`, 
        },
      });
      setSuppliers(res.data.suppliers || []);
    } catch (err) {
      console.error("Error fetching suppliers:", err);
    }
  };

  fetchSuppliers();
}, []);


  const navigate = useNavigate();

  const handleBooking = (supplierId) => {
    // Navigate to booking page with supplier id
    navigate(`/customer/booking/${supplierId}`);
  };



  return (
    <Row>
  {suppliers.map((supplier, index) => (
    <Col key={index} md={4} className="mb-4">
      <Card>
        <Card.Body>
          <Card.Title>{supplier.name}</Card.Title>
          <Card.Text>
            <strong>Location:</strong> {supplier.location} <br />
            <strong>Price Per Unit:</strong> {supplier.pricePerUnit} <br />
            <strong>Available Units:</strong> {supplier.availableUnits} <br />
            <strong>Renewable:</strong> {supplier.renewable ? "✅ Yes" : "❌ No"}
          </Card.Text>
          <Button
            onClick={() => handleBooking(supplier._id)}
            variant="primary"
            size="sm"
          >
            Book
          </Button>
        </Card.Body>
      </Card>
    </Col>
  ))}
</Row>
    
  );
}

export default DashboardCustomer;
