import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Container, Card, Button } from "react-bootstrap";

export default function SupplierRequestDetails() {
  const { requestId } = useParams();
  const [request, setRequest] = useState(null);

  useEffect(() => {
    const fetchRequest = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/request/status/${requestId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setRequest(res.data.request);
      } catch (err) {
        console.error("Error fetching request details:", err);
      }
    };
    fetchRequest();
  }, [requestId]);

  if (!request) return <p>Loading...</p>;

  return (
    <Container className="mt-5">
      <Card className="p-4 shadow-sm">
        <h3>üîã Charging Request Details</h3>

        <p><b>Customer Name:</b> {request.customer?.name}</p>
        <p><b>Phone:</b> {request.customer?.phone}</p>
        <p><b>Units Requested:</b> {request.unitsRequested}</p>
        <p><b>Status:</b> {request.status}</p>

        {/* ‚úÖ Show customer location (if available) */}
        {request.customer?.location && (
          <p><b>Customer Location:</b> {request.customer.location}</p>
        )}

        {/* ‚úÖ Optional supplier view of coordinates */}
        {request.customer?.coordinates?.lat && request.customer?.coordinates?.lng && (
          <a
            href={`https://www.google.com/maps?q=${request.customer.coordinates.lat},${request.customer.coordinates.lng}`}
            target="_blank"
            rel="noopener noreferrer"
            className="btn btn-info mt-2"
          >
            üìç View Customer Location
          </a>
        )}

        <Button variant="success" className="mt-3">
          ‚úÖ Mark as Charging Started
        </Button>
      </Card>
    </Container>
  );
}
