// src/pages/DashboardCustomer.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, Button, Alert, Form, Spinner } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

function DashboardCustomer() {
  const [suppliers, setSuppliers] = useState([]);
  const [message, setMessage] = useState("");
  const [darkMode, setDarkMode] = useState(false);
  const [searchQuery, setsearchQuery] = useState("");
  const [customerLocation, setCustomerLocation] = useState(null);
  const [loadingSuppliers, setLoadingSuppliers] = useState(true);

  const navigate = useNavigate();

  // Get real-time customer location on load (Option A)
  useEffect(() => {
  if ("geolocation" in navigator) {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setCustomerLocation({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        });
      },
      (error) => {
        console.error("Location error:", error);
        alert("Please enable location to sort suppliers by distance.");
      }
    );
  }
}, []);


  useEffect(() => {
    const dm = localStorage.getItem("darkMode") === "true";
    setDarkMode(dm);

    if (dm) document.body.classList.add("dark-mode");
    else document.body.classList.remove("dark-mode");
  }, []);

  // Haversine Distance Function
  const getDistance = (lat1, lon1, lat2, lon2) => {
    if (
      lat1 === undefined ||
      lon1 === undefined ||
      lat2 === undefined ||
      lon2 === undefined ||
      lat1 === null ||
      lon1 === null ||
      lat2 === null ||
      lon2 === null
    ) {
      return Infinity;
    }
    const R = 6371; // km
    const dLat = (lat2 - lat1) * (Math.PI / 180);
    const dLon = (lon2 - lon1) * (Math.PI / 180);

    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos((lat1 * Math.PI) / 180) *
        Math.cos((lat2 * Math.PI) / 180) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2);

    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  // Fetch suppliers & sort after we have customerLocation (or fetch anyway if no location)
  useEffect(() => {
    const fetchSuppliers = async () => {
      setLoadingSuppliers(true);
      try {
        const res = await axios.get("http://localhost:5000/api/customer/dashboard", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });

        let activeSuppliers = (res.data.suppliers || []).filter((s) => s.isOnline);

        // Compute distance if we have customerLocation and supplier coords
        if (customerLocation && customerLocation.lat && customerLocation.lng) {
          activeSuppliers = activeSuppliers.map((s) => {
            const sLat = s.coordinates?.lat;
            const sLng = s.coordinates?.lng;
            const dist =
              sLat != null && sLng != null
                ? getDistance(customerLocation.lat, customerLocation.lng, sLat, sLng)
                : Infinity;
            return { ...s, distance: dist };
          });

          activeSuppliers.sort((a, b) => (a.distance ?? Infinity) - (b.distance ?? Infinity));
        } else {
          // if no customer location, still keep suppliers as-is but set distance Infinity (so UI won't show)
          activeSuppliers = activeSuppliers.map((s) => ({ ...s, distance: Infinity }));
        }

        setSuppliers(activeSuppliers);
      } catch (err) {
        console.error("Error fetching suppliers:", err);
        setMessage("Failed to load suppliers.");
      } finally {
        setLoadingSuppliers(false);
      }
    };

    fetchSuppliers();
  }, [customerLocation]);

  // Filtered suppliers (search)
  const filteredSuppliers = suppliers.filter((s) =>
    (s.name || "").toLowerCase().includes(searchQuery.toLowerCase()) ||
    (s.location || "").toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Booking handler — updates backend location (Option B) then creates request
  const handleBooking = async (supplierId) => {
    const customerId = localStorage.getItem("userId");
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("role");

    if (!token || role !== "CUSTOMER") {
      alert("Please log in as a customer first.");
      return;
    }

    const unitsRequested = prompt("Enter number of units to request:");
    if (!unitsRequested) return;

    try {
      // Option B: send current GPS to backend again before creating request
      if (customerLocation && customerLocation.lat && customerLocation.lng) {
        try {
          await axios.post(
            "http://localhost:5000/api/request/update-location",
            { customerId, lat: customerLocation.lat, lng: customerLocation.lng },
            { headers: { Authorization: `Bearer ${token}` } }
          );
        } catch (err) {
          console.warn("Warning: failed to update location before booking", err?.response?.data || err.message);
          // proceed even if update failed
        }
      }

      const res = await axios.post(
        "http://localhost:5000/api/request/create",
        { customerId, supplierId, unitsRequested },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert("✅ Request sent! Waiting for supplier approval...");
      localStorage.setItem("requestId", res.data.request._id);
      navigate(`/customer/booking/${res.data.request._id}`);
    } catch (err) {
      alert(err.response?.data?.message || "Failed to send request.");
    }
  };

  return (
    <Container className="mt-5">
      <h2 className={darkMode ? "text-light" : "text-dark"}>Customer Dashboard</h2>

      <Form.Control
        type="text"
        placeholder="Search supplier by name or location..."
        className="my-3"
        value={searchQuery}
        onChange={(e) => setsearchQuery(e.target.value)}
      />

      {message && <Alert variant="info">{message}</Alert>}

      {loadingSuppliers ? (
        <div className="text-center my-4">
          <Spinner animation="border" />
        </div>
      ) : (
        <Row>
          {filteredSuppliers.length === 0 && (
            <p className="text-muted text-center mt-3">No suppliers match your search.</p>
          )}

          {filteredSuppliers.map((supplier) => (
            <Col key={supplier._id} md={4} className="mb-4">
              <Card className={darkMode ? "bg-dark text-light border-secondary" : ""}>
                <Card.Body>
                  <Card.Title>{supplier.name}</Card.Title>
                  <Card.Text>
                    <strong>Location:</strong> {supplier.location} <br />
                    <strong>Price Per Unit:</strong> ₹{supplier.pricePerUnit} <br />
                    <strong>Available Units:</strong> {supplier.availableUnits} <br />
                    {supplier.distance !== undefined && supplier.distance !== Infinity ? (
                      <span>
                        <strong>Distance:</strong> {supplier.distance.toFixed(2)} km
                      </span>
                    ) : null}
                  </Card.Text>

                  <Button onClick={() => handleBooking(supplier._id)}>Book</Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}

export default DashboardCustomer;
