// =====================
// DashboardCustomer.jsx
// =====================
import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Alert,
  Form,
  Spinner,
} from "react-bootstrap";
import { useNavigate } from "react-router-dom";

export default function DashboardCustomer() {
  const [suppliers, setSuppliers] = useState([]);
  const [message, setMessage] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [customerLocation, setCustomerLocation] = useState(undefined);
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate();

  // --------------------------
  // HAVERSINE DISTANCE (KM)
  // --------------------------
  const haversineKm = useCallback((lat1, lon1, lat2, lon2) => {
    if (![lat1, lon1, lat2, lon2].every(Number.isFinite)) return Infinity;

    const toRad = (d) => (d * Math.PI) / 180;
    const R = 6371;

    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);

    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(toRad(lat1)) *
        Math.cos(toRad(lat2)) *
        Math.sin(dLon / 2) ** 2;

    return R * 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  }, []);

  // --------------------------
  // FETCH CUSTOMER GPS
  // --------------------------
  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setCustomerLocation({
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        });
      },
      () => setCustomerLocation(null),
      { enableHighAccuracy: true, timeout: 8000 }
    );
  }, []);

  // --------------------------
  // FETCH SUPPLIERS + SORT BY DISTANCE
  // --------------------------
  useEffect(() => {
    if (customerLocation === undefined) return;

    const loadSuppliers = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem("token");

        const res = await axios.get(
          "http://localhost:5000/api/customer/dashboard",
          { headers: { Authorization: `Bearer ${token}` } }
        );

        let list = res.data.suppliers || [];

        // compute distance
        list = list.map((s) => {
          const slat = s?.coordinates?.lat;
          const slng = s?.coordinates?.lng;

          let dist = Infinity;

          if (
            customerLocation &&
            Number.isFinite(customerLocation.lat) &&
            Number.isFinite(customerLocation.lng) &&
            Number.isFinite(slat) &&
            Number.isFinite(slng)
          ) {
            dist = haversineKm(
              customerLocation.lat,
              customerLocation.lng,
              slat,
              slng
            );
          }

          return { ...s, distance: dist };
        });

        // sort ascending by distance
        list.sort((a, b) => a.distance - b.distance);

        setSuppliers(list);
      } catch (err) {
        console.error(err);
        setMessage("Failed to load suppliers");
      }
      setLoading(false);
    };

    loadSuppliers();
  }, [customerLocation, haversineKm]);

  // --------------------------
  // SEARCH FILTER
  // --------------------------
  const filtered = suppliers.filter(
    (s) =>
      s.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      s.location?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <Container className="mt-4">
      <h2>Customer Dashboard</h2>

      <Form.Control
        className="my-3"
        placeholder="Search by name or location..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {loading ? (
        <div className="text-center my-4">
          <Spinner animation="border" />
        </div>
      ) : (
        <Row>
          {filtered.map((s) => (
            <Col key={s._id} md={4} className="mb-4">
              <Card>
                <Card.Body>
                  <Card.Title>{s.name}</Card.Title>

                  <Card.Text>
                    <strong>Location:</strong> {s.location} <br />
                    <strong>Price:</strong> â‚¹{s.pricePerUnit} <br />
                    <strong>Available Units:</strong> {s.availableUnits} <br />

                    {Number.isFinite(s.distance) && (
                      <>
                        <strong>Distance:</strong>{" "}
                        {s.distance.toFixed(2)} km
                      </>
                    )}
                  </Card.Text>

                  <Button>Book</Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}
