import React, { useEffect, useState } from "react";
import axios from "axios";
import { Container, Table } from "react-bootstrap";

function DashboardCustomer() {
  const [suppliers, setSuppliers] = useState([]);

  useEffect(() => {
  const fetchSuppliers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/customer/dashboard", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`, 
        },
      });
      setSuppliers(res.data.suppliers);
    } catch (err) {
      console.error("Error fetching suppliers:", err);
    }
  };

  fetchSuppliers();
}, []);


  return (
    <Container className="mt-5">
      <h2>Customer Dashboard</h2>
      <p>Here you can find charging stations, send requests, and track your history.</p>
       <h2 className="mb-4 text-center">Available Suppliers</h2>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Name</th>
            <th>Phone</th>
            <th>Location</th>
            <th>Price Per Unit</th>
            <th>Available Units</th>
            <th>Renewable</th>
          </tr>
        </thead>
        <tbody>
          {suppliers.map((supplier, index) => (
            <tr key={index}>
              <td>{supplier.name}</td>
              <td>{supplier.phone}</td>
              <td>{supplier.location}</td>
              <td>{supplier.pricePerUnit}</td>
              <td>{supplier.availableUnits}</td>
              <td>{supplier.renewable ? "✅ Yes" : "❌ No"}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>

    
  );
}

export default DashboardCustomer;
