import React, { useEffect, useState } from "react";
import { Container, Row, Col, Button, Alert, Modal } from "react-bootstrap";
import axios from "axios";
import io from "socket.io-client";
import { useNavigate } from "react-router-dom";
import { AnimatePresence, motion } from "frame-motionS";
import "react-toastify/dist/ReactToastify.css";


// Connect to backend WebSocket server
const socket = io("http://localhost:5000"); // âš ï¸ change this to your backend URL if deployed


function DashboardSupplier() {
  const [supplierId, setSupplierId] = useState("");
  const [requests, setRequests] = useState([]);
  const [showStats, setShowStats] = useState(false);
  const [stats, setStats] = useState(null);
  const navigate = useNavigate();


  // ðŸ”¹ On component mount
  useEffect(() => {
  const id = localStorage.getItem("supplierId");

  console.log("Supplier ID from localStorage:", id);

  if (!id) {
    console.error("âŒ No supplierId found in localStorage!");
    return;
  }

  setSupplierId(id);
  socket.emit("joinSupplierRoom", id);
  console.log("ðŸ“¡ Emitted joinSupplierRoom for:", id);

 socket.on("newChargingRequest", (data) => {
  console.log("ðŸ“¡ Received new request:", data);
  toast.info(`ðŸ”‹ New request from ${data.customerName} (${data.unitsNeeded} units)`, {
    position: "top-right",
    autoClose: 4000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    theme: "colored",
  });
  setRequests((prev) => [...prev, data]);
});


  return () => socket.off("newChargingRequest");
}, []);


  // ðŸ”¹ Fetch supplier statistics when button clicked
  const fetchStats = async () => {
    try {
      const response = await axios.get(`/api/suppliers/stats/${supplierId}`);
      setStats(response.data);
      setShowStats(true);
    } catch (err) {
      console.error("Error loading stats:", err);
    }
  };

  // ðŸ”¹ Supplier accepts request
  const handleAccept = async (requestId) => {
    try {
      await axios.post(`/api/requests/accept/${requestId}`, { supplierId });
      alert("âœ… Request accepted! Customer can now access your charging station.");
      setRequests((prev) => prev.filter((r) => r._id !== requestId));
    } catch (err) {
      alert("âŒ Error accepting request");
    }
  };

  // ðŸ”¹ Supplier rejects request
  const handleReject = async (requestId) => {
    try {
      await axios.post(`/api/requests/reject/${requestId}`);
      alert("ðŸš« Request rejected.");
      setRequests((prev) => prev.filter((r) => r._id !== requestId));
    } catch (err) {
      alert("Error rejecting request");
    }
  };

  return (
    <Container className="mt-5">
      {/* Dashboard Header */}
      <Row>
        <Col>
          <h2>âš¡ Supplier Dashboard</h2>
          <p>Monitor customer requests and manage your charging station.</p>
        </Col>
        <Col className="text-end">
          <Button variant="info" onClick={() => navigate("/supplier/statistics")}>
            ðŸ“Š View Statistics
          </Button>
        </Col>
      </Row>

      <hr />

      {/* ðŸ”” Floating Request Notifications */}
      <div
        style={{
          position: "fixed",
          top: "90px",
          right: "30px",
          zIndex: 2000,
          width: "340px",
        }}
      >
        <AnimatePresence>
          {requests.map((req) => (
            <motion.div
              key={req._id}
              initial={{ opacity: 0, x: 100 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 100 }}
              transition={{ duration: 0.4 }}
            >
              <Alert
                variant="info"
                className="shadow-sm rounded-3 mt-3"
                style={{
                  backgroundColor: "#e8f4ff",
                  borderLeft: "5px solid #007bff",
                }}
              >
                <strong>ðŸ”‹ New Request</strong>
                <p className="mb-1 mt-1">
                  From: <b>{req.customerName}</b>
                </p>
                <p className="mb-2">
                  Units Needed: {req.unitsNeeded} <br />
                  Distance: {req.distance?.toFixed(2)} km
                </p>
                <div className="d-flex justify-content-end">
                  <Button
                    size="sm"
                    variant="success"
                    className="me-2"
                    onClick={() => handleAccept(req._id)}
                  >
                    Accept
                  </Button>
                  <Button
                    size="sm"
                    variant="danger"
                    onClick={() => handleReject(req._id)}
                  >
                    Reject
                  </Button>
                </div>
              </Alert>
            </motion.div>
          ))}
        </AnimatePresence>
      </div>

      {/* Statistics Modal */}
      <Modal show={showStats} onHide={() => setShowStats(false)}>
        <Modal.Header closeButton>
          <Modal.Title>ðŸ“ˆ Charging Station Statistics</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {stats ? (
            <>
              <p><b>Total Requests:</b> {stats.totalRequests}</p>
              <p><b>Total Units Supplied:</b> {stats.totalUnitsSupplied}</p>
              <p><b>Total Revenue:</b> â‚¹{stats.totalRevenue}</p>
            </>
          ) : (
            <p>Loading...</p>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowStats(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}

export default DashboardSupplier;
