import React, { useEffect, useState } from "react";
import { Container, Row, Col, Button, Card, Modal } from "react-bootstrap";
import axios from "axios";
import io from "socket.io-client";
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

// ‚úÖ Connect once globally
const socket = io("http://localhost:5000", {
  transports: ["websocket"],
  reconnection: true,
});

function DashboardSupplier() {
  const [supplierId, setSupplierId] = useState("");
  const [requests, setRequests] = useState([]);
  const [showStats, setShowStats] = useState(false);
  const [stats, setStats] = useState(null);
  const navigate = useNavigate();

  // üîπ On component mount
  useEffect(() => {
    const id = localStorage.getItem("supplierId");
    console.log("Supplier ID from localStorage:", id);

    if (!id) {
      console.error("‚ùå No supplierId found in localStorage!");
      return;
    }

    setSupplierId(id);

    socket.emit("joinSupplierRoom", id);
    console.log("üì° Emitted joinSupplierRoom for:", id);

    // ‚úÖ Listen for new charging requests
    socket.on("newChargingRequest", (data) => {
      console.log("üì° Received new request:", data);

      // üîî Show popup on screen
      toast.info(
        `üîã New request from ${data.customerName} (${data.unitsNeeded} units)`,
        {
          position: "top-right",
          autoClose: 4000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          theme: "colored",
        }
      );

      // Add to UI list
      setRequests((prev) => [...prev, data]);
    });

    return () => {
      socket.off("newChargingRequest");
    };
  }, []);

  // üîπ Fetch supplier statistics
  const fetchStats = async () => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/suppliers/stats/${supplierId}`
      );
      setStats(response.data);
      setShowStats(true);
    } catch (err) {
      console.error("Error loading stats:", err);
    }
  };

  // üîπ Supplier accepts request
  const handleAccept = async (requestId) => {
    try {
      await axios.post(
        `http://localhost:5000/api/requests/accept/${requestId}`,
        { supplierId }
      );
      alert("‚úÖ Request accepted! Customer can now access your charging station.");
      setRequests((prev) => prev.filter((r) => r._id !== requestId));
    } catch (err) {
      alert("‚ùå Error accepting request");
    }
  };

  // üîπ Supplier rejects request
  const handleReject = async (requestId) => {
    try {
      await axios.post(`http://localhost:5000/api/requests/reject/${requestId}`);
      alert("üö´ Request rejected.");
      setRequests((prev) => prev.filter((r) => r._id !== requestId));
    } catch (err) {
      alert("Error rejecting request");
    }
  };

  return (
    <Container className="mt-5">
      <ToastContainer /> {/* ‚úÖ Enables visible toast popups */}

      {/* Dashboard Header */}
      <Row>
        <Col>
          <h2>‚ö° Supplier Dashboard</h2>
          <p>Monitor customer requests and manage your charging station.</p>
        </Col>
        <Col className="text-end">
          <Button variant="info" onClick={fetchStats}>
            üìä View Statistics
          </Button>
        </Col>
      </Row>

      <hr />

      {/* Incoming Requests */}
      <h4>Incoming Charging Requests</h4>
      {requests.length === 0 ? (
        <p>No new requests yet...</p>
      ) : (
        requests.map((req) => (
          <Card key={req._id} className="mt-3 shadow-sm p-3">
            <Card.Body>
              <Card.Title>üîã Request from {req.customerName}</Card.Title>
              <Card.Text>
                <strong>Units Needed:</strong> {req.unitsNeeded} <br />
                <strong>Distance:</strong> {req.distance?.toFixed(2)} km
              </Card.Text>

              <div>
                <Button
                  variant="success"
                  className="me-2"
                  onClick={() => handleAccept(req._id)}
                >
                  ‚úÖ Accept
                </Button>
                <Button
                  variant="danger"
                  onClick={() => handleReject(req._id)}
                >
                  ‚ùå Reject
                </Button>
              </div>
            </Card.Body>
          </Card>
        ))
      )}

      {/* Statistics Modal */}
      <Modal show={showStats} onHide={() => setShowStats(false)}>
        <Modal.Header closeButton>
          <Modal.Title>üìà Charging Station Statistics</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {stats ? (
            <>
              <p><b>Total Requests:</b> {stats.totalRequests}</p>
              <p><b>Total Units Supplied:</b> {stats.totalUnitsSupplied}</p>
              <p><b>Total Revenue:</b> ‚Çπ{stats.totalRevenue}</p>
            </>
          ) : (
            <p>Loading...</p>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowStats(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}

export default DashboardSupplier;
