// src/components/ProfileSlidePanel.jsx
import React from "react";
import { motion } from "framer-motion";
import { Button, Form } from "react-bootstrap";

export default function ProfileSlidePanel({ show, onClose, user }) {
  return (
    <>
      {/* Background dim */}
      {show && (
        <div
          onClick={onClose}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            background: "rgba(0,0,0,0.4)",
            zIndex: 999,
          }}
        />
      )}

      {/* Sliding panel */}
      <motion.div
        initial={{ x: "100%" }}
        animate={{ x: show ? 0 : "100%" }}
        transition={{ type: "tween", duration: 0.4 }}
        style={{
          position: "fixed",
          top: 0,
          right: 0,
          width: "350px",
          height: "100vh",
          background: "#fff",
          boxShadow: "-4px 0 10px rgba(0,0,0,0.2)",
          zIndex: 1000,
          padding: "20px",
        }}
      >
        <h3 className="mb-4">My Profile</h3>

        <Form>
          <Form.Group className="mb-3">
            <Form.Label>Name</Form.Label>
            <Form.Control defaultValue={user?.name} />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Phone</Form.Label>
            <Form.Control defaultValue={user?.phone} />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Location</Form.Label>
            <Form.Control defaultValue={user?.location} />
          </Form.Group>

          <Button variant="primary" className="w-100">Save Changes</Button>
        </Form>

        <Button
          variant="secondary"
          className="mt-3 w-100"
          onClick={onClose}
        >
          Close
        </Button>
      </motion.div>
    </>
  );
}
