// src/pages/DashboardCustomer.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, Button, Alert, Form, Spinner } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

function DashboardCustomer() {
  const [suppliers, setSuppliers] = useState([]);
  const [message, setMessage] = useState("");
  const [darkMode, setDarkMode] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [customerLocation, setCustomerLocation] = useState(undefined); // undefined = loading, null = denied
  const [loadingSuppliers, setLoadingSuppliers] = useState(true);

  const navigate = useNavigate();

  // Robust haversine implementation (returns km)
  const haversine = (lat1, lon1, lat2, lon2) => {
    // Ensure inputs are numbers
    lat1 = Number(lat1); lon1 = Number(lon1); lat2 = Number(lat2); lon2 = Number(lon2);
    if (![lat1,lon1,lat2,lon2].every(Number.isFinite)) return NaN;

    const toRad = (deg) => (deg * Math.PI) / 180;
    const R = 6371; // km
    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * Math.sin(dLon / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  /* ----------------------------
     GET CUSTOMER LOCATION
     --------------------------- */
  useEffect(() => {
    let fallbackTimer;
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          clearTimeout(fallbackTimer);
          const lat = pos.coords.latitude;
          const lng = pos.coords.longitude;
          console.log("Customer GPS:", lat, lng);
          setCustomerLocation({ lat, lng });
        },
        (err) => {
          clearTimeout(fallbackTimer);
          console.warn("Geolocation error:", err);
          alert("Location access denied or failed. Suppliers will not be sorted by distance.");
          setCustomerLocation(null);
        },
        { enableHighAccuracy: true, timeout: 10000 }
      );

      fallbackTimer = setTimeout(() => {
        if (customerLocation === undefined) {
          console.log("GPS timeout -> continuing without location");
          setCustomerLocation(null);
        }
      }, 4000);
    } else {
      alert("Browser does not support geolocation.");
      setCustomerLocation(null);
    }
    return () => clearTimeout(fallbackTimer);
  }, []);

  /* ----------------------------
     DARK MODE
     --------------------------- */
  useEffect(() => {
    const dm = localStorage.getItem("darkMode") === "true";
    setDarkMode(dm);
    document.body.classList.toggle("dark-mode", dm);
  }, []);

  /* ----------------------------
     FETCH SUPPLIERS & COMPUTE DISTANCES
     --------------------------- */
  useEffect(() => {
    if (customerLocation === undefined) return; // still waiting for GPS

    const fetchSuppliers = async () => {
      setLoadingSuppliers(true);
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:5000/api/customer/dashboard", {
          headers: { Authorization: `Bearer ${token}` },
        });

        let list = (res.data.suppliers || []).filter((s) => s.isOnline);

        // Log raw suppliers for debugging
        console.log("Raw suppliers from API:", list);

        if (customerLocation && Number.isFinite(customerLocation.lat) && Number.isFinite(customerLocation.lng)) {
          // compute distance for each supplier
          list = list.map((s) => {
            // read coordinates robustly — handle both possible shapes
            let slat = s?.coordinates?.lat ?? s?.latitude ?? s?.lat ?? null;
            let slng = s?.coordinates?.lng ?? s?.longitude ?? s?.lng ?? null;

            // sometimes values are strings — coerce to Number if possible
            if (slat !== null) slat = Number(slat);
            if (slng !== null) slng = Number(slng);

            // If coords missing or invalid, try swapping (in case saved swapped)
            let distance = NaN;
            if (Number.isFinite(slat) && Number.isFinite(slng)) {
              distance = haversine(customerLocation.lat, customerLocation.lng, slat, slng);
            } else {
              // attempt swap guess (if only one present or wrong order)
              const maybeLat = Number(slng), maybeLng = Number(slat);
              if (Number.isFinite(maybeLat) && Number.isFinite(maybeLng)) {
                distance = haversine(customerLocation.lat, customerLocation.lng, maybeLat, maybeLng);
                // mark that swap was used so you can inspect
                s._swapped = true;
              }
            }

            // attach debug fields so you can inspect in console
            return {
              ...s,
              __rawLat: slat,
              __rawLng: slng,
              distance: Number.isFinite(distance) ? distance : Infinity,
            };
          });

          // debug table
          console.table(
            list.map((x) => ({
              name: x.name,
              rawLat: x.__rawLat,
              rawLng: x.__rawLng,
              distance: x.distance === Infinity ? "∞" : x.distance.toFixed(3),
              swapped: x._swapped ? true : false,
            }))
          );

          list.sort((a, b) => (a.distance ?? Infinity) - (b.distance ?? Infinity));
        } else {
          // no customer location — set infinite distance
          list = list.map((s) => ({ ...s, distance: Infinity }));
        }

        setSuppliers(list);
      } catch (err) {
        console.error("Error loading suppliers:", err);
        setMessage("Failed to load suppliers.");
      }
      setLoadingSuppliers(false);
    };

    fetchSuppliers();
  }, [customerLocation]);

  /* ----------------------------
     FILTER
     --------------------------- */
  const filteredSuppliers = suppliers.filter((s) =>
    (s.name || "").toLowerCase().includes(searchQuery.toLowerCase()) ||
    (s.location || "").toLowerCase().includes(searchQuery.toLowerCase())
  );

  /* ----------------------------
     BOOK
     --------------------------- */
  const handleBooking = async (supplierId) => {
    const customerId = localStorage.getItem("userId");
    const token = localStorage.getItem("token");
    const unitsRequested = prompt("Enter units:");
    if (!unitsRequested) return;
    try {
      if (customerLocation?.lat && customerLocation?.lng) {
        await axios.post(
          "http://localhost:5000/api/request/update-location",
          { customerId, lat: customerLocation.lat, lng: customerLocation.lng },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      }
      const res = await axios.post(
        "http://localhost:5000/api/request/create",
        { customerId, supplierId, unitsRequested },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Request sent successfully!");
      navigate(`/customer/booking/${res.data.request._id}`);
    } catch (err) {
      alert(err.response?.data?.message || "Booking failed");
    }
  };

  /* ----------------------------
     UI
     --------------------------- */
  return (
    <Container className="mt-5">
      <h2 className={darkMode ? "text-light" : "text-dark"}>Customer Dashboard</h2>

      <Form.Control
        type="text"
        className="my-3"
        placeholder="Search by name or location..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {message && <Alert variant="info">{message}</Alert>}

      {loadingSuppliers ? (
        <div className="text-center my-4"><Spinner animation="border" /></div>
      ) : (
        <Row>
          {filteredSuppliers.length === 0 && <p className="text-muted text-center mt-3">No suppliers found.</p>}

          {filteredSuppliers.map((s) => (
            <Col key={s._id} md={4} className="mb-4">
              <Card className={darkMode ? "bg-dark text-light border-secondary" : ""}>
                <Card.Body>
                  <Card.Title>{s.name}</Card.Title>
                  <Card.Text>
                    <strong>Location:</strong> {s.location} <br />
                    <strong>Price:</strong> ₹{s.pricePerUnit} <br />
                    <strong>Available Units:</strong> {s.availableUnits} <br />
                    {s.distance !== Infinity && (
                      <span><strong>Distance:</strong> {s.distance.toFixed(2)} km</span>
                    )}
                    {/* show debug lat/lng if needed */}
                    {/* <div style={{fontSize:12,color:'#666'}}>lat:{s.__rawLat} lng:{s.__rawLng} swapped:{s._swapped? 'yes':'no'}</div> */}
                  </Card.Text>
                  <Button onClick={() => handleBooking(s._id)}>Book</Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}

export default DashboardCustomer;
