import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, Button } from "react-bootstrap";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  LineElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Tooltip,
  Legend,
} from "chart.js";
import axios from "axios";
import { useNavigate } from "react-router-dom";

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

function SupplierStatistics() {
  const [stats, setStats] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const supplierId = localStorage.getItem("supplierId");
    if (!supplierId) {
      navigate("/supplier/statistics");
      return;
    }

    const fetchStats = async () => {
      try {
        const res = await axios.get(`/api/suppliers/stats/${supplierId}`);
        setStats(res.data);
      } catch (err) {
        console.error("Error fetching stats:", err);
      }
    };

    fetchStats();
  }, [navigate]);

  if (!stats) {
    return <p className="text-center mt-5">Loading statistics...</p>;
  }

  // Chart data
  const chartData = {
    labels: stats.months || ["Jan", "Feb", "Mar", "Apr"],
    datasets: [
      {
        label: "Revenue (‚Çπ)",
        data: stats.monthlyRevenue || [0, 0, 0, 0],
        fill: false,
        borderColor: "#007bff",
        tension: 0.3,
      },
    ],
  };

  return (
    <Container className="mt-5">
      <Row className="mb-4">
        <Col>
          <h2>üìà Charging Station Statistics</h2>
          <p>Monitor your revenue, activity, and performance trends.</p>
        </Col>
        <Col className="text-end">
          <Button variant="secondary" onClick={() => navigate(-1)}>
            ‚Üê Back to Dashboard
          </Button>
        </Col>
      </Row>

      {/* Summary cards */}
      <Row>
        <Col md={4}>
          <Card className="p-3 mb-3 shadow-sm">
            <h5>Total Requests</h5>
            <h2>{stats.totalRequests}</h2>
          </Card>
        </Col>
        <Col md={4}>
          <Card className="p-3 mb-3 shadow-sm">
            <h5>Total Units Supplied</h5>
            <h2>{stats.totalUnitsSupplied}</h2>
          </Card>
        </Col>
        <Col md={4}>
          <Card className="p-3 mb-3 shadow-sm">
            <h5>Total Revenue</h5>
            <h2>‚Çπ{stats.totalRevenue}</h2>
          </Card>
        </Col>
      </Row>

      {/* Chart Section */}
      <Card className="p-4 shadow-sm">
        <h5>Revenue Trend (Monthly)</h5>
        <Line data={chartData} />
      </Card>
    </Container>
  );
}

export default SupplierStatistics;
