import React, { useState } from "react";
import { Navbar, Nav, Container } from "react-bootstrap";
import { Link } from "react-router-dom";
import ProfileSlidePanel from "./ProfileSlidePanel";

function NavigationBar() {
  const [showProfile, setShowProfile] = useState(false);
  const loggedIn = localStorage.getItem("token");

  return (
    <>
      <Navbar bg="dark" variant="dark" expand="lg">
        <Container>
          <Navbar.Brand as={Link} to="/">Coolgrid EV</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ms-auto">

              {!loggedIn ? (
                <>
                  <Nav.Link as={Link} to="/">Home</Nav.Link>
                  <Nav.Link as={Link} to="/login">Login</Nav.Link>
                  <Nav.Link as={Link} to="/register/customer">Register Customer</Nav.Link>
                  <Nav.Link as={Link} to="/register/supplier">Register Supplier</Nav.Link>
                </>
              ) : (
                <>
                  <Nav.Link as={Link} to="/">Home</Nav.Link>

                  <Nav.Link onClick={() => setShowProfile(true)}>
                    ðŸ‘¤ My Profile
                  </Nav.Link>

                  <Nav.Link
                    onClick={() => {
                      localStorage.clear();
                      window.location.href = "/login";
                    }}
                  >
                    ðŸšª Logout
                  </Nav.Link>
                </>
              )}

            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* Sliding Panel */}
      <ProfileSlidePanel
        show={showProfile}
        onClose={() => setShowProfile(false)}
        user={{
          name: localStorage.getItem("name") || "User",
          phone: localStorage.getItem("phone") || "Not provided",
          location: localStorage.getItem("location") || "Not provided",
        }}
      />
    </>
  );
}

export default NavigationBar;
