import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Container, Spinner, Card, Button, Alert } from "react-bootstrap";

const BACKEND = "http://localhost:5000";

function BookingPage() {
  const { supplierId } = useParams();
  const [requestId, setRequestId] = useState(null);
  const [status, setStatus] = useState("CREATING");
  const [supplierInfo, setSupplierInfo] = useState(null);
  const [otp, setOtp] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    const createRequest = async () => {
      try {
        const customerId = localStorage.getItem("userId");
        const token = localStorage.getItem("token");

        if (!customerId || !token) {
          setError("Please log in as a customer first.");
          return;
        }

        const res = await axios.post(
          `${BACKEND}/api/request/create`,
          { customerId, supplierId, unitsRequested: 20 },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        setRequestId(res.data.request._id);
        setStatus("WAITING");
      } catch (err) {
        console.error("Error creating request:", err);
        setError("Failed to create request.");
      }
    };

    createRequest();
  }, [supplierId]);

  // Polling for supplier response
  useEffect(() => {
    if (!requestId) return;

    const interval = setInterval(async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(`${BACKEND}/api/request/status/${requestId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const req = res.data.request;
        if (req.status === "ACCEPTED") {
          setSupplierInfo(req.supplier);
          setOtp(req.otp);
          setStatus("ACCEPTED");
          clearInterval(interval);
        } else if (req.status === "REJECTED") {
          setStatus("REJECTED");
          clearInterval(interval);
        }
      } catch (err) {
        console.error("Error checking status:", err);
      }
    }, 5000);

    return () => clearInterval(interval);
  }, [requestId]);

  return (
    <Container className="mt-5 text-center">
      <h2>‚ö° Booking Request</h2>
      {error && <Alert variant="danger">{error}</Alert>}

      {status === "CREATING" && (
        <div className="mt-5">
          <Spinner animation="border" variant="primary" />
          <p>Creating your request...</p>
        </div>
      )}

      {status === "WAITING" && (
        <div className="mt-5">
          <Spinner animation="grow" variant="info" />
          <h4 className="mt-3">Waiting for supplier approval...</h4>
        </div>
      )}

      {status === "ACCEPTED" && supplierInfo && (
        <Card className="p-4 mt-4 shadow-sm">
          <h3>‚úÖ Request Accepted!</h3>
          <p>Use the OTP below at the charging station.</p>
          <h4 className="text-success">üî¢ OTP: {otp}</h4>
          <hr />
          <h5>Supplier Details</h5>
          <p><b>Name:</b> {supplierInfo.name}</p>
          <p><b>Phone:</b> {supplierInfo.phone}</p>
          <p><b>Location:</b> {supplierInfo.location}</p>

          <Button
            variant="primary"
            href={`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(
              supplierInfo.location
            )}`}
            target="_blank"
          >
            üìç Open in Google Maps
          </Button>
        </Card>
      )}

      {status === "REJECTED" && (
        <Alert variant="danger" className="mt-5">
          ‚ùå Your request was rejected by the supplier. Please try another station.
        </Alert>
      )}
    </Container>
  );
}

export default BookingPage;
