// src/pages/RegisterSupplier.jsx
import React, { useState } from "react";
import { Container, Form, Button } from "react-bootstrap";
import axios from "axios";
import { useNavigate } from "react-router-dom";

/** Keep local list in sync with backend AREAS mapping (keys must match) */
const AREAS = [
  "JP Nagar",
  "KR Market",
  "BTM Layout",
  "Banashankari",
  "Silk Board",
  "Majestic",
  "Electronic City",
];

function RegisterSupplier() {
  const [form, setForm] = useState({
    username: "",
    password: "",
    name: "",
    phone: "",
    pricePerUnit: "",
    availableUnits: "",
    renewable: false,
    area: AREAS[0],
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((f) => ({ ...f, [name]: type === "checkbox" ? checked : value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.username || !form.password || !form.name || !form.phone) {
      alert("Please fill required fields");
      return;
    }
    if (!form.renewable) {
      alert("You must confirm renewable energy usage");
      return;
    }

    const payload = {
      username: form.username,
      password: form.password,
      name: form.name,
      phone: form.phone,
      renewable: form.renewable,
      pricePerUnit: Number(form.pricePerUnit || 0),
      availableUnits: Number(form.availableUnits || 0),
      area: form.area, // backend will map area -> coordinates
    };

    try {
      await axios.post("http://localhost:5000/api/auth/register/supplier", payload);
      alert("Supplier registered successfully");
      navigate("/login");
    } catch (err) {
      console.error("Register supplier error:", err.response?.data || err);
      alert(err.response?.data?.message || "Registration failed");
    }
  };

  return (
    <Container className="mt-5" style={{ maxWidth: 700 }}>
      <h2 className="mb-4 text-center">Register as Supplier</h2>
      <Form onSubmit={handleSubmit}>
        {[
          ["username", "text"],
          ["password", "password"],
          ["name", "text"],
          ["phone", "text"],
          ["pricePerUnit", "number"],
          ["availableUnits", "number"],
        ].map(([field, type]) => (
          <Form.Group className="mb-3" key={field}>
            <Form.Label>{field}</Form.Label>
            <Form.Control
              required
              name={field}
              type={type}
              value={form[field]}
              onChange={handleChange}
            />
          </Form.Group>
        ))}

        <Form.Group className="mb-3">
          <Form.Label>Area</Form.Label>
          <Form.Select name="area" value={form.area} onChange={handleChange}>
            {AREAS.map((a) => (
              <option key={a} value={a}>
                {a}
              </option>
            ))}
          </Form.Select>
          <Form.Text className="text-muted">
            Select the area where your station is located (predefined areas).
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Check
            name="renewable"
            checked={form.renewable}
            onChange={handleChange}
            type="checkbox"
            label="I use renewable energy (required)"
          />
        </Form.Group>

        <Button type="submit" className="w-100" variant="success">
          Register
        </Button>
      </Form>
    </Container>
  );
}

export default RegisterSupplier;
