import React, { useState } from "react";
import { Container, Form, Button } from "react-bootstrap";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function RegisterSupplier() {
  const [form, setForm] = useState({
    username: "",
    password: "",
    name: "",
    phone: "",
    location: "",
    pricePerUnit: "",
    availableUnits: "",
    renewable: false,
    latitude: "",
    longitude: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === "checkbox" ? checked : value });
  };

  const getCoordinates = () => {
    if (!navigator.geolocation) {
      alert("Geolocation not supported");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setForm({
          ...form,
          latitude: pos.coords.latitude,
          longitude: pos.coords.longitude,
        });
        alert("Location captured successfully!");
      },
      () => alert("Location denied!")
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.latitude || !form.longitude) {
      alert("Please click 'Get Location' to set coordinates.");
      return;
    }

    const payload = {
      ...form,
      pricePerUnit: Number(form.pricePerUnit),
      availableUnits: Number(form.availableUnits),
      latitude: Number(form.latitude),
      longitude: Number(form.longitude),
    };

    try {
      await axios.post("http://localhost:5000/api/auth/register/supplier", payload);
      alert("Supplier registered successfully!");
      navigate("/login");
    } catch (err) {
      alert(err.response?.data?.message || "Registration failed");
    }
  };

  return (
    <Container className="mt-5" style={{ maxWidth: "600px" }}>
      <h2 className="text-center mb-4">Register as Supplier</h2>

      <Form onSubmit={handleSubmit}>

        {/* Input Fields */}
        {[
          ["username", "text"],
          ["password", "password"],
          ["name", "text"],
          ["phone", "text"],
          ["location", "text"],
          ["pricePerUnit", "number"],
          ["availableUnits", "number"],
        ].map(([field, type], i) => (
          <Form.Group className="mb-3" key={i}>
            <Form.Label>{field}</Form.Label>
            <Form.Control
              type={type}
              name={field}
              value={form[field]}
              onChange={handleChange}
            />
          </Form.Group>
        ))}

        {/* Renewable Checkbox */}
        <Form.Group className="mb-3">
          <Form.Check
            type="checkbox"
            name="renewable"
            checked={form.renewable}
            onChange={handleChange}
            label="I use renewable energy (required)"
          />
        </Form.Group>

        {/* Location Capture */}
        <Form.Group>
          <Button variant="info" onClick={getCoordinates} className="mb-3">
            Get Location (Lat/Lng)
          </Button>

          <Form.Control
            className="mb-2"
            placeholder="Latitude"
            value={form.latitude}
            readOnly
          />
          <Form.Control
            className="mb-3"
            placeholder="Longitude"
            value={form.longitude}
            readOnly
          />
        </Form.Group>

        <Button variant="success" type="submit" className="w-100">
          Register
        </Button>
      </Form>
    </Container>
  );
}

export default RegisterSupplier;
