import React, { useEffect, useState } from "react";
import { Container, Row, Col, Button, Alert, Modal } from "react-bootstrap";
import axios from "axios";
import io from "socket.io-client";
import { useNavigate } from "react-router-dom";
import { AnimatePresence, motion } from "framer-motion";

const socket = io("http://localhost:5000");

function DashboardSupplier() {
  const [supplierId, setSupplierId] = useState("");
  const [requests, setRequests] = useState([]);
  const [showStats, setShowStats] = useState(false);
  const [stats, setStats] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const id = localStorage.getItem("supplierId");
    setSupplierId(id);

    if (id) {
      socket.emit("joinSupplierRoom", id);
      console.log("ðŸ“¡ Joined supplier room:", id);
    }

    socket.on("newChargingRequest", (data) => {
      console.log("ðŸ“¡ New Request:", data);
      setRequests((prev) => [...prev, data]);
    });

    return () => socket.off("newChargingRequest");
  }, []);

  const fetchStats = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/suppliers/stats/${supplierId}`);
      setStats(res.data);
      setShowStats(true);
    } catch (err) {
      console.error("Error loading stats:", err);
    }
  };

  const handleAccept = async (requestId) => {
    try {
      const res = await axios.post(
        `http://localhost:5000/api/request/accept/${requestId}`,
        { supplierId },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      alert("âœ… Request accepted!");
      navigate(`/supplier/request-details/${res.data.request._id}`);
    } catch (err) {
      alert("âŒ Error accepting request");
    }
  };

  const handleReject = async (requestId) => {
    try {
      await axios.post(
        `http://localhost:5000/api/request/reject/${requestId}`,
        {},
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      alert("ðŸš« Request rejected.");
      setRequests((prev) => prev.filter((r) => r._id !== requestId));
    } catch (err) {
      alert("Error rejecting request");
    }
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col>
          <h2>âš¡ Supplier Dashboard</h2>
          <p>Monitor customer requests and manage your charging station.</p>
        </Col>
        <Col className="text-end">
          <Button variant="info" onClick={fetchStats}>
            ðŸ“Š View Statistics
          </Button>
        </Col>
      </Row>
      <hr />

      {/* ðŸ”” Floating Requests */}
      <div style={{ position: "fixed", top: "90px", right: "30px", zIndex: 2000, width: "340px" }}>
        <AnimatePresence>
          {requests.map((req) => (
            <motion.div key={req._id} initial={{ opacity: 0, x: 100 }} animate={{ opacity: 1, x: 0 }}>
              <Alert variant="info" className="shadow-sm rounded-3 mt-3">
                <strong>ðŸ”‹ New Request from {req.customerName}</strong>
                <p>Units Needed: {req.unitsNeeded}</p>
                <p>Distance: {req.distance?.toFixed(2)} km</p>
                <Button size="sm" variant="success" onClick={() => handleAccept(req._id)}>
                  Accept
                </Button>{" "}
                <Button size="sm" variant="danger" onClick={() => handleReject(req._id)}>
                  Reject
                </Button>
              </Alert>
            </motion.div>
          ))}
        </AnimatePresence>
      </div>

      {/* Stats Modal */}
      <Modal show={showStats} onHide={() => setShowStats(false)}>
        <Modal.Header closeButton>
          <Modal.Title>ðŸ“ˆ Charging Station Statistics</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {stats ? (
            <>
              <p><b>Total Requests:</b> {stats.totalRequests}</p>
              <p><b>Total Units Supplied:</b> {stats.totalUnitsSupplied}</p>
              <p><b>Total Revenue:</b> â‚¹{stats.totalRevenue}</p>
            </>
          ) : (
            <p>Loading...</p>
          )}
        </Modal.Body>
      </Modal>
    </Container>
  );
}

export default DashboardSupplier;
