import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import io from "socket.io-client";
import axios from "axios";

const socket = io("http://localhost:5000");

function BookingPage() {
  const { requestId } = useParams();
  const [status, setStatus] = useState("PENDING");
  const [otp, setOtp] = useState("");
  const [supplier, setSupplier] = useState(null);

  useEffect(() => {
    const customerId = localStorage.getItem("customerId");

    // Join the customer‚Äôs private room
    if (customerId) {
      socket.emit("joinCustomerRoom", customerId);
      console.log("üì° Joined customer room:", customerId);
    }

    // Listen for supplier acceptance
    socket.on("requestAccepted", (data) => {
      console.log("‚úÖ Request accepted:", data);
      setStatus("ACCEPTED");
      setOtp(data.otp);
      setSupplier({
        name: data.supplierName,
        phone: data.supplierPhone,
        location: data.supplierLocation,
      });
    });

    socket.on("requestRejected", () => {
      setStatus("REJECTED");
    });

    return () => {
      socket.off("requestAccepted");
      socket.off("requestRejected");
    };
  }, []);

  return (
    <div className="container mt-5 text-center">
      <h3>‚ö° Booking Request</h3>
      {status === "PENDING" && (
        <p>‚è≥ Waiting for supplier approval...</p>
      )}
      {status === "ACCEPTED" && supplier && (
        <div className="mt-3">
          <h5>‚úÖ Supplier Accepted!</h5>
          <p><b>Supplier:</b> {supplier.name}</p>
          <p><b>Phone:</b> {supplier.phone}</p>
          <p><b>OTP:</b> <span style={{ color: "green", fontSize: "1.4rem" }}>{otp}</span></p>
          {supplier.coordinates && supplier.location.lat && supplier.location.lng && (
          <a
            href={`https://www.google.com/maps?q=${supplier.location.lat},${supplier.location.lng}`}
            target="_blank"
            rel="noopener noreferrer"
            className="btn btn-success mt-2"
          >
            üìç Trace Supplier Location
          </a>
        )}

        </div>
      )}
      {status === "REJECTED" && (
        <p className="text-danger">üö´ Supplier rejected your request.</p>
      )}
    </div>
  );
}

export default BookingPage;
