import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Container, Card, Button, Form, Alert, Spinner } from "react-bootstrap";
import io from "socket.io-client";

const BACKEND = "http://localhost:5000";
const socket = io(BACKEND, { autoConnect: true });

export default function SupplierRequestDetails() {
  const { requestId } = useParams();
  const [request, setRequest] = useState(null);
  const [enteredOtp, setEnteredOtp] = useState("");
  const [verificationMsg, setVerificationMsg] = useState("");
  const [loading, setLoading] = useState(false);
  const [paymentMode, setPaymentMode] = useState("");
  const [paymentConfirmed, setPaymentConfirmed] = useState(false);
  const [message, setMessage] = useState("");

  // ‚úÖ Fetch Request Data
  useEffect(() => {
    const fetchRequest = async () => {
      try {
        const res = await axios.get(
          `${BACKEND}/api/request/status/${requestId}`,
          { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
        );
        setRequest(res.data.request);
      } catch (err) {
        console.error("Error fetching request details:", err);
      }
    };
    fetchRequest();

    const supplierId =
      localStorage.getItem("supplierId") || localStorage.getItem("userId");
    if (supplierId) {
      socket.emit("joinSupplierRoom", supplierId);
      console.log("üì° Joined supplier room:", supplierId);
    }

    // ‚úÖ Listen for customer payment selections
    socket.on("cashPaymentChosen", (data) => {
      console.log("üíµ Customer selected Cash on Delivery:", data);
      setPaymentMode("CASH");
      setMessage(data.message);
    });

    socket.on("upiPaymentDone", (data) => {
      console.log("üí≥ UPI Payment Done:", data);
      setPaymentMode("UPI");
      setMessage(data.message);
      setTimeout(() => (window.location.href = "/dashboard/supplier"), 3000);
    });

    

    

    return () => {
      socket.off("cashPaymentChosen");
      socket.off("upiPaymentDone");
      socket.off("paymentConfirmed");

    };
  }, [requestId]);

  // ‚úÖ Verify OTP
  const handleOtpVerify = async () => {
    if (!enteredOtp) return alert("Please enter the OTP first");
    setLoading(true);
    try {
      const res = await axios.post(
        `${BACKEND}/api/request/verify-otp/${requestId}`,
        { enteredOtp },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );

      if (res.data.success) {
        setVerificationMsg("‚úÖ OTP verified successfully!");
      } else {
        setVerificationMsg("‚ùå Invalid OTP. Please try again.");
      }
    } catch (err) {
      console.error("Error verifying OTP:", err);
      setVerificationMsg("‚ö†Ô∏è Error verifying OTP.");
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ Trace Customer Location
  const handleTraceCustomer = () => {
    if (
      request?.customer?.coordinates?.lat &&
      request?.customer?.coordinates?.lng
    ) {
      window.open(
        `https://www.google.com/maps?q=${request.customer.coordinates.lat},${request.customer.coordinates.lng}`,
        "_blank"
      );
    } else {
      alert("Customer location not available yet.");
    }
  };

  // ‚úÖ Start Charging
  const handleStartCharging = async () => {
    try {
      const res = await axios.post(
        `${BACKEND}/api/request/start-charging/${requestId}`,
        {},
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      alert(res.data.message);
    } catch (err) {
      console.error("Error starting charging:", err);
      alert("Error starting charging");
    }
  };

  // ‚úÖ Complete Charging
  const handleCompleteCharging = async () => {
    try {
      const res = await axios.post(
        `${BACKEND}/api/request/complete-charging/${requestId}`,
        {},
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      alert(res.data.message);
    } catch (err) {
      console.error("Error completing charging:", err);
      alert("Error completing charging");
    }
  };

  // ‚úÖ Confirm Payment (For COD)
  const handleConfirmPayment = async () => {
    try {
      const totalAmount =
        (request?.supplier?.pricePerUnit || 0) *
        (request?.unitsRequested || 0);

      await axios.put(`${BACKEND}/api/request/${requestId}/markPaid`, {
        mode: "CASH_CONFIRMED",
        amountPaid: totalAmount,
        transactionId: "CASH-" + Date.now(),
      });

      setPaymentConfirmed(true);
      alert("‚úÖ Payment confirmed successfully!");
      window.location.href = "/dashboard/supplier";
    } catch (err) {
      console.error("‚ùå Error confirming payment:", err);
      alert("Error confirming payment");
    }
  };

  if (!request)
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" />
        <p>Loading request details...</p>
      </Container>
    );

  return (
    <Container className="mt-5">
      <Card className="p-4 shadow-sm">
        <h3>üîã Charging Request Details</h3>
        <p><b>Customer Name:</b> {request.customer?.name}</p>
        <p><b>Phone:</b> {request.customer?.phone}</p>
        <p><b>Units Requested:</b> {request.unitsRequested}</p>
        <p><b>Status:</b> {request.status}</p>

        {/* ‚úÖ OTP Verification Section */}
        <Form className="mt-3">
          <Form.Group controlId="otpInput">
            <Form.Label><b>Enter Customer OTP:</b></Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter OTP provided by customer"
              value={enteredOtp}
              onChange={(e) => setEnteredOtp(e.target.value)}
              style={{ maxWidth: "250px", margin: "auto" }}
            />
          </Form.Group>

          <Button
            variant="primary"
            className="mt-3"
            onClick={handleOtpVerify}
            disabled={loading}
          >
            {loading ? "Verifying..." : "Verify OTP"}
          </Button>
        </Form>

        {verificationMsg && (
          <Alert
            className="mt-3 text-center"
            variant={verificationMsg.includes("‚úÖ") ? "success" : "danger"}
          >
            {verificationMsg}
          </Alert>
        )}

        {/* ‚úÖ Trace Customer Location */}
        <div className="text-center mt-4">
          <Button variant="success" onClick={handleTraceCustomer}>
            üìç Trace Customer Location
          </Button>
        </div>

        {/* ‚úÖ Charging Controls */}
        {verificationMsg.includes("‚úÖ") && (
          <div className="text-center mt-4">
            <Button variant="warning" className="me-2" onClick={handleStartCharging}>
              ‚ö° Start Charging
            </Button>
            <Button variant="success" onClick={handleCompleteCharging}>
              ‚úÖ Mark as Completed
            </Button>
          </div>
        )}

        {/* ‚úÖ Payment Section */}
        {message && (
          <Alert
            className="mt-4 text-center"
            variant={paymentMode === "CASH" ? "warning" : "success"}
          >
            {message}
          </Alert>
        )}

        {paymentMode === "CASH" && !paymentConfirmed && (
          <div className="text-center mt-3">
            <p>üíµ Customer selected Cash on Delivery.</p>
            <Button variant="success" onClick={handleConfirmPayment}>
              ‚úÖ Payment Received
            </Button>
          </div>
        )}

        {paymentConfirmed && (
          <Alert className="mt-4" variant="success">
            ‚úÖ Payment confirmed! Redirecting...
          </Alert>
        )}
      </Card>
    </Container>
  );
}
