// src/pages/DashboardCustomer.jsx
import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import { Container, Row, Col, Card, Button, Alert, Form, Spinner } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

export default function DashboardCustomer() {
  const [suppliers, setSuppliers] = useState([]);
  const [message, setMessage] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [customerLocation, setCustomerLocation] = useState(undefined); // undefined = waiting, null = denied
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Haversine in km
  const haversineKm = useCallback((lat1, lon1, lat2, lon2) => {
    const toNum = (v) => (v === undefined || v === null ? NaN : Number(v));
    lat1 = toNum(lat1); lon1 = toNum(lon1); lat2 = toNum(lat2); lon2 = toNum(lon2);
    if (![lat1, lon1, lat2, lon2].every(Number.isFinite)) return NaN;
    const toRad = (deg) => (deg * Math.PI) / 180;
    const R = 6371;
    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);
    const a = Math.sin(dLat/2)**2 + Math.cos(toRad(lat1))*Math.cos(toRad(lat2))*Math.sin(dLon/2)**2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  }, []);

  // Get GPS (ONLY) once on load (with a small timeout/fallback)
  useEffect(() => {
    let timer;
    if (!("geolocation" in navigator)) {
      setCustomerLocation(null);
      return;
    }

    const onSuccess = (pos) => {
      clearTimeout(timer);
      setCustomerLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude });
    };
    const onFail = (err) => {
      clearTimeout(timer);
      console.warn("geolocation failed", err);
      setCustomerLocation(null);
    };

    navigator.geolocation.getCurrentPosition(onSuccess, onFail, {
      enableHighAccuracy: true, timeout: 8000, maximumAge: 0
    });

    timer = setTimeout(() => {
      if (customerLocation === undefined) setCustomerLocation(null);
    }, 6000);

    return () => clearTimeout(timer);
  }, []);

  // fetch suppliers + compute distances when we know (or don't) customerLocation
  useEffect(() => {
    if (customerLocation === undefined) return; // still waiting for GPS

    const fetchSuppliers = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem("token");
        const resp = await axios.get("http://localhost:5000/api/customer/dashboard", {
          headers: { Authorization: `Bearer ${token}` },
        });

        let list = (resp.data.suppliers || []).filter((s) => s.isOnline);

        // compute distance if customer gps available and supplier coordinates exist
        if (customerLocation && Number.isFinite(customerLocation.lat) && Number.isFinite(customerLocation.lng)) {
          list = list.map((s) => {
            const slat = s?.coordinates?.lat ?? s?.latitude ?? null;
            const slng = s?.coordinates?.lng ?? s?.longitude ?? null;
            const distance = (slat != null && slng != null) ? haversineKm(customerLocation.lat, customerLocation.lng, slat, slng) : Infinity;
            return { ...s, distance: Number.isFinite(distance) ? distance : Infinity };
          });

          list.sort((a,b) => (a.distance ?? Infinity) - (b.distance ?? Infinity));
        } else {
          // no gps: set distance Infinity so UI hides it
          list = list.map(s => ({ ...s, distance: Infinity }));
        }

        setSuppliers(list);
      } catch (err) {
        console.error("Error fetching suppliers", err);
        setMessage("Failed to load suppliers.");
      } finally {
        setLoading(false);
      }
    };

    fetchSuppliers();
  }, [customerLocation, haversineKm]);

  const filtered = suppliers.filter(s =>
    (s.name || "").toLowerCase().includes(searchQuery.toLowerCase()) ||
    (s.area || s.location || "").toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleBooking = async (supplierId) => {
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("role");
    const customerId = localStorage.getItem("userId");
    if (!token || role !== "CUSTOMER") {
      return alert("Please login as customer");
    }
    const unitsRequested = prompt("Enter units:");
    if (!unitsRequested) return;
    try {
      // optionally update customer location server-side
      if (customerLocation && Number.isFinite(customerLocation.lat) && Number.isFinite(customerLocation.lng)) {
        await axios.post("http://localhost:5000/api/request/update-location",
          { customerId, lat: customerLocation.lat, lng: customerLocation.lng },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      }
      const res = await axios.post("http://localhost:5000/api/request/create",
        { customerId, supplierId, unitsRequested },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Request created");
      localStorage.setItem("requestId", res.data.request._id);
      navigate(`/customer/booking/${res.data.request._id}`);
    } catch (err) {
      console.error("booking failed", err);
      alert(err?.response?.data?.message || "Booking failed");
    }
  };

  return (
    <Container className="mt-5">
      <h2>Customer Dashboard</h2>

      <Form.Control placeholder="Search by name or area" className="my-3" value={searchQuery} onChange={e=>setSearchQuery(e.target.value)} />

      {loading ? (
        <div className="text-center my-4"><Spinner animation="border" /></div>
      ) : (
        <Row>
          {filtered.length === 0 ? <p className="text-muted">No suppliers found.</p> : filtered.map(s => (
            <Col md={4} key={s._id} className="mb-4">
              <Card>
                <Card.Body>
                  <Card.Title>{s.name}</Card.Title>
                  <Card.Text>
                    <strong>Location:</strong> {s.area ?? s.location ?? "—"} <br />
                    <strong>Price:</strong> ₹{s.pricePerUnit} <br />
                    <strong>Units:</strong> {s.availableUnits} <br />
                    {Number.isFinite(s.distance) && s.distance !== Infinity && (
                      <span><strong>Distance:</strong> {s.distance.toFixed(2)} km</span>
                    )}
                  </Card.Text>
                  <Button onClick={() => handleBooking(s._id)}>Book</Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
      {message && <Alert variant="info">{message}</Alert>}
    </Container>
  );
}
