import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Container, Card, Button, Form,Alert } from "react-bootstrap";

export default function SupplierRequestDetails() {
  const { requestId } = useParams();
  const [request, setRequest] = useState(null);
   const [enteredOtp, setEnteredOtp] = useState("");
  const [verificationMsg, setVerificationMsg] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchRequest = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/request/status/${requestId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setRequest(res.data.request);
      } catch (err) {
        console.error("Error fetching request details:", err);
      }
    };
    fetchRequest();
  }, [requestId]);

  // ‚úÖ Handle OTP verification
  const handleOtpVerify = async () => {
    if (!enteredOtp) return alert("Please enter the OTP first");
    setLoading(true);
    try {
      const res = await axios.post(
        `http://localhost:5000/api/request/verify-otp/${requestId}`,
        { enteredOtp },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );

      if (res.data.success) {
        setVerificationMsg("‚úÖ OTP verified successfully!");
      } else {
        setVerificationMsg("‚ùå Invalid OTP. Please try again.");
      }
    } catch (err) {
      console.error("Error verifying OTP:", err);
      setVerificationMsg("‚ö†Ô∏è Error verifying OTP.");
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ Trace customer location (if available)
  const handleTraceCustomer = () => {
    if (
      request?.customer?.coordinates?.lat &&
      request?.customer?.coordinates?.lng
    ) {
      window.open(
        `https://www.google.com/maps?q=${request.customer.coordinates.lat},${request.customer.coordinates.lng}`,
        "_blank"
      );
    } else {
      alert("Customer location not available yet.");
    }
  };

  const handleStartCharging = async () => {
  try {
    const res = await axios.post(
      `http://localhost:5000/api/request/start-charging/${requestId}`,
      {},
      { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
    );
    alert(res.data.message);
  } catch (err) {
    console.error("Error starting charging:", err);
    alert("Error starting charging");
  }
};

const handleCompleteCharging = async () => {
  try {
    const res = await axios.post(
      `http://localhost:5000/api/request/complete-charging/${requestId}`,
      {},
      { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
    );
    alert(res.data.message);
  } catch (err) {
    console.error("Error completing charging:", err);
    alert("Error completing charging");
  }
};


  if (!request) return <p>Loading...</p>;

  return (
    <Container className="mt-5">
      <Card className="p-4 shadow-sm">
        <h3>üîã Charging Request Details</h3>

        <p><b>Customer Name:</b> {request.customer?.name}</p>
        <p><b>Phone:</b> {request.customer?.phone}</p>
        <p><b>Units Requested:</b> {request.unitsRequested}</p>
        <p><b>Status:</b> {request.status}</p>

         {/* ‚úÖ OTP Verification Section */}
        <Form className="mt-3">
          <Form.Group controlId="otpInput">
            <Form.Label><b>Enter Customer OTP:</b></Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter OTP provided by customer"
              value={enteredOtp}
              onChange={(e) => setEnteredOtp(e.target.value)}
              style={{ maxWidth: "250px", margin: "auto" }}
            />
          </Form.Group>

          <Button
            variant="primary"
            className="mt-3"
            onClick={handleOtpVerify}
            disabled={loading}
          >
            {loading ? "Verifying..." : "Verify OTP"}
          </Button>
        </Form>

        {verificationMsg && (
          <Alert
            className="mt-3 text-center"
            variant={verificationMsg.includes("‚úÖ") ? "success" : "danger"}
          >
            {verificationMsg}
          </Alert>
        )}

        {/* ‚úÖ Trace Customer Location */}
        <div className="text-center mt-4">
          <Button variant="success" onClick={handleTraceCustomer}>
            üìç Trace Customer Location
          </Button>
        </div>
        {verificationMsg.includes("‚úÖ") && (
            <div className="text-center mt-4">
              {request.status === "VERIFIED" && (
              <Button
                variant="warning"
                className="me-2"
                onClick={handleStartCharging}
              >
                ‚ö° Start Charging
              </Button>
              )}
              <Button
                variant="success"
                onClick={handleCompleteCharging}
              >
                ‚úÖ Mark as Completed
              </Button>
            </div>
          )}

      </Card>
    </Container>
  );
}
