// src/pages/SupplierRequestDetails.jsx
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Container, Card, Button, Form, Alert, Spinner } from "react-bootstrap";
import io from "socket.io-client";
import { motion } from "framer-motion"; 

const BACKEND = "http://localhost:5000";
const socket = io(BACKEND, { autoConnect: true });

export default function SupplierRequestDetails() {
  const { requestId } = useParams();

  const [request, setRequest] = useState(null);
  const [enteredOtp, setEnteredOtp] = useState("");
  const [verificationMsg, setVerificationMsg] = useState("");
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState("");


  const [paymentMode, setPaymentMode] = useState("");
  const [paymentConfirmed, setPaymentConfirmed] = useState(false);
  const [message, setMessage] = useState("");
  const [chargingStarted, setChargingStarted] = useState(false);
const [chargingCompleted, setChargingCompleted] = useState(false);


  // ---------- Load Request Details ----------
  useEffect(() => {
    const fetchRequest = async () => {
      try {
        const res = await axios.get(
          `${BACKEND}/api/request/status/${requestId}`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        setRequest(res.data.request);
      } catch (err) {
        console.error("‚ùå Error fetching request details:", err);
      }
      
    };

    fetchRequest();

    // Supplier joins the SAME room as customer
    socket.emit("joinRoom", { room: requestId });
    console.log("üì° Supplier joined room:", requestId);

    

    // Listen for cash payment
    socket.on("cashPaymentChosen", (data) => {
      if (data.requestId === requestId) {
        console.log("üíµ Customer selected Cash:", data);
        setPaymentMode("CASH");
        setMessage("Customer has chosen Cash on Delivery. Collect payment.");
      }
    });

    // Listen for UPI
    socket.on("upiPaymentDone", (data) => {
      if (data.requestId === requestId) {
        console.log("üí≥ UPI Payment Done:", data);
        setPaymentMode("UPI");
        setMessage("Customer completed UPI payment.");
      }
    });

    // Listen for final confirmation
    socket.on("paymentConfirmedByCustomer", (data) => {
      if (data.requestId === requestId) {
        console.log("üü¢ Payment Confirmed By Customer:", data);
        setPaymentConfirmed(true);
        setMessage("Payment confirmed by customer.");
      }
    });

    return () => {
      socket.off("cashPaymentChosen");
      socket.off("upiPaymentDone");
      socket.off("paymentConfirmedByCustomer");
    };
  }, [requestId]);

  const handleVerifyOtp = async () => {
  try {
    if (!enteredOtp) {
      alert("Please enter OTP");
      return;
    }

    console.log("Request ID:", requestId);
    console.log("Entered OTP:", enteredOtp);

    const res = await axios.post(
      `${BACKEND}/api/request/verify-otp/${requestId}`,
      { enteredOtp },
      {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`
        }
      }
    );

    setVerificationMsg("OTP Verified Successfully!");
  } catch (err) {
    console.log("OTP verify error:", err.response?.data);
    setVerificationMsg(err.response?.data?.message || "Invalid OTP");
  }
};

const handleStartCharging = async () => {
  try {
    const token = localStorage.getItem("token");

    const res =await axios.post(`${BACKEND}/api/request/start-charging/${requestId}`, 
      {}, 
      {
        headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`
        }
     });
     setChargingStarted(true);
     setChargingCompleted(false);


    console.log("Start charging response:", res.data);
    setStatus("CHARGING");
  } catch (err) {
    console.error("Error starting charging:", err);
  }
};

const handleCompleteCharging = async () => {
  try {
    const token = localStorage.getItem("token");

    const res = await axios.post(
      `http://localhost:5000/api/request/complete-charging/${requestId}`,
      {},
      { headers: { Authorization: `Bearer ${token}` } }
    );

    
      setChargingCompleted(true);
    console.log("Complete charging response:", res.data);
    setStatus("COMPLETED");
  } catch (err) {
    console.error("Error completing charging:", err);
  }
};



  // ---------- Supplier Confirms Payment ----------
  const handleSupplierConfirmPayment = () => {
    socket.emit("paymentConfirmedBySupplier", {
      requestId,
      customerId: request.customerId,
      supplierId: localStorage.getItem("supplierId"),
      message: "Supplier confirmed payment",
    });

    setPaymentConfirmed(true);
    setMessage("You confirmed the payment.");
  };

  // -------------------- UI --------------------
  return (
  <Container className="mt-5">
    {!request ? (
      <Spinner animation="border" />
    ) : (
      <Card className="p-4 shadow">
        <h3>üîå Request Details</h3>

        <p><b>Customer:</b> {request.customerName}</p>
        <p><b>Units Needed:</b> {request.unitsNeeded}</p>
        <p><b>Status:</b> {request.status}</p>

        <hr />

        <h4>üîê OTP Verification</h4>

        {/* OTP message */}
        {verificationMsg && (
          <Alert variant="info">{verificationMsg}</Alert>
        )}

        {/* OTP Input + verify only when status is APPROVED or pending */}
        {request.status !== "COMPLETED" && !paymentMode && (
          <>
            <Form.Group>
              <Form.Label>Enter OTP sent to customer</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter OTP"
                value={enteredOtp}
                onChange={(e) => setEnteredOtp(e.target.value)}
              />
            </Form.Group>

            <Button
              className="mt-3"
              variant="primary"
              disabled={loading}
              onClick={handleVerifyOtp}
            >
              {loading ? "Verifying..." : "Verify OTP"}
            </Button>
          </>
        )}

        <hr />

        {/* ‚≠ê Show Start Charging + Complete Charging buttons AFTER OTP Verified */}
{/* -------------------------------------------------- */}
{/*            üîã Animated Charging Controls           */}
{/* -------------------------------------------------- */}

<div className="p-4">
      <h3>Charging Controls</h3>

      {/* Start Charging Button */}
      <motion.div
        whileTap={{ scale: 0.95 }}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.3 }}
        className="mb-3"
      >
        <Button
          variant="success"
          onClick={handleStartCharging}
          disabled={chargingStarted}     // üî• Disables after clicked
          style={{ width: "200px" }}
        >
          {chargingStarted ? "Charging Started ‚úî" : "Start Charging"}
        </Button>
      </motion.div>

      {/* Complete Charging Button */}
      <motion.div
        whileTap={{ scale: 0.95 }}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.3, delay: 0.2 }}
      >
        <Button
          variant="primary"
          onClick={handleCompleteCharging}
          disabled={!chargingStarted || chargingCompleted} // üî• Enabled only after start
          style={{ width: "200px" }}
        >
          {chargingCompleted ? "Charging Completed ‚úî" : "Complete Charging"}
        </Button>
      </motion.div>

      {/* Status Message */}
      <div className="mt-4">
        {chargingCompleted ? (
          <Alert variant="success">Charging fully completed!</Alert>
        ) : chargingStarted ? (
          <Alert variant="info">Charging in progress...</Alert>
        ) : (
          <Alert variant="warning">Waiting to start charging...</Alert>
        )}
      </div>
    </div>

        <h4>üí≥ Payment Status</h4>

        {message && <Alert variant="info">{message}</Alert>}

        {/* Waiting for customer payment selection */}
        {!paymentMode }

        {/* Cash Mode */}
        {paymentMode === "CASH" && (
          <Alert variant="success">
            Customer selected Cash on Delivery. Collect payment & confirm.
          </Alert>
        )}

        {/* UPI Mode */}
        {paymentMode === "UPI" && (
          <Alert variant="success">
            UPI Payment done by customer. Confirm to proceed.
          </Alert>
        )}

        {/* Supplier confirm payment button */}
        {(paymentMode === "UPI" || paymentMode === "CASH") &&
          !paymentConfirmed && (
            <Button
              variant="success"
              className="mt-3"
              onClick={handleSupplierConfirmPayment}
            >
              Confirm Payment
            </Button>
          )}

        {/* After confirmation */}
        {paymentConfirmed && (
          <Alert variant="success" className="mt-3">
            ‚úî Payment confirmed
          </Alert>
        )}
      </Card>
    )}
  </Container>
);
}
