// src/pages/DashboardCustomer.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, Button, Alert, Form, Spinner } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

function DashboardCustomer() {
  const [suppliers, setSuppliers] = useState([]);
  const [message, setMessage] = useState("");
  const [darkMode, setDarkMode] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [customerLocation, setCustomerLocation] = useState(null);
  const [loadingSuppliers, setLoadingSuppliers] = useState(true);

  const navigate = useNavigate();

  /* ------------------------------------------
     1️⃣  GET CUSTOMER LOCATION ON LOAD
  ------------------------------------------- */
  useEffect(() => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setCustomerLocation({
            lat: pos.coords.latitude,
            lng: pos.coords.longitude,
          });
        },
        () => {
          alert("Please enable location to sort suppliers by distance.");
        }
      );
    }
  }, []);

  /* ------------------------------------------
     2️⃣  APPLY DARK MODE
  ------------------------------------------- */
  useEffect(() => {
    const dm = localStorage.getItem("darkMode") === "true";
    setDarkMode(dm);
    document.body.classList.toggle("dark-mode", dm);
  }, []);

  /* ------------------------------------------
     3️⃣  HAVERSINE DISTANCE FUNCTION
  ------------------------------------------- */
  const getDistance = (lat1, lon1, lat2, lon2) => {
    const toRad = (x) => (x * Math.PI) / 180;
    const R = 6371;
    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);

    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(toRad(lat1)) *
        Math.cos(toRad(lat2)) *
        Math.sin(dLon / 2) ** 2;

    return R * 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  };

  /* ------------------------------------------
     4️⃣  FETCH SUPPLIERS & SORT BY DISTANCE
  ------------------------------------------- */
  useEffect(() => {
    const fetchSuppliers = async () => {
      setLoadingSuppliers(true);

      try {
        const res = await axios.get("http://localhost:5000/api/customer/dashboard", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });

        let active = (res.data.suppliers || []).filter((s) => s.isOnline);

        // If we have GPS, calculate distance
        if (customerLocation?.lat && customerLocation?.lng) {
          active = active.map((s) => {
            const lat = s.coordinates?.lat;
            const lng = s.coordinates?.lng;

            const dist = lat && lng
              ? getDistance(customerLocation.lat, customerLocation.lng, lat, lng)
              : Infinity;

            return { ...s, distance: dist };
          });

          // Sort smallest → largest
          active.sort((a, b) => a.distance - b.distance);
        } else {
          // No location → no sorting
          active = active.map((s) => ({ ...s, distance: Infinity }));
        }

        setSuppliers(active);
      } catch (err) {
        console.error("Error fetching suppliers:", err);
        setMessage("Failed to load suppliers.");
      }

      setLoadingSuppliers(false);
    };

    fetchSuppliers();
  }, [customerLocation]); // whenever location updates, suppliers re-fetch

  /* ------------------------------------------
     5️⃣  FILTER SUPPLIERS
  ------------------------------------------- */
  const filteredSuppliers = suppliers.filter((s) =>
    (s.name || "").toLowerCase().includes(searchQuery.toLowerCase()) ||
    (s.location || "").toLowerCase().includes(searchQuery.toLowerCase())
  );

  /* ------------------------------------------
     6️⃣  BOOK SUPPLIER
  ------------------------------------------- */
  const handleBooking = async (supplierId) => {
    const customerId = localStorage.getItem("userId");
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("role");

    if (role !== "CUSTOMER") {
      alert("Please login as a customer.");
      return;
    }

    const unitsRequested = prompt("Enter number of units:");
    if (!unitsRequested) return;

    try {
      // Send customer live GPS to backend
      if (customerLocation?.lat && customerLocation?.lng) {
        await axios.post(
          "http://localhost:5000/api/request/update-location",
          { customerId, lat: customerLocation.lat, lng: customerLocation.lng },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      }

      const res = await axios.post(
        "http://localhost:5000/api/request/create",
        { customerId, supplierId, unitsRequested },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert("Request sent!");
      localStorage.setItem("requestId", res.data.request._id);
      navigate(`/customer/booking/${res.data.request._id}`);
    } catch (err) {
      alert(err.response?.data?.message || "Failed to send request.");
    }
  };

  /* ------------------------------------------
     7️⃣  UI
  ------------------------------------------- */
  return (
    <Container className="mt-5">
      <h2 className={darkMode ? "text-light" : "text-dark"}>Customer Dashboard</h2>

      <Form.Control
        type="text"
        placeholder="Search supplier by name or location..."
        className="my-3"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {message && <Alert variant="info">{message}</Alert>}

      {loadingSuppliers ? (
        <div className="text-center my-4">
          <Spinner animation="border" />
        </div>
      ) : (
        <Row>
          {filteredSuppliers.length === 0 && (
            <p className="text-muted text-center mt-3">No suppliers found.</p>
          )}

          {filteredSuppliers.map((s) => (
            <Col key={s._id} md={4} className="mb-4">
              <Card className={darkMode ? "bg-dark text-light border-secondary" : ""}>
                <Card.Body>
                  <Card.Title>{s.name}</Card.Title>

                  <Card.Text>
                    <strong>Location:</strong> {s.location} <br />
                    <strong>Price:</strong> ₹{s.pricePerUnit} <br />
                    <strong>Available Units:</strong> {s.availableUnits} <br />
                    {s.distance !== Infinity && (
                      <span><strong>Distance:</strong> {s.distance.toFixed(2)} km</span>
                    )}
                  </Card.Text>

                  <Button onClick={() => handleBooking(s._id)}>Book</Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}

export default DashboardCustomer;
