// src/pages/DashboardCustomer.jsx
import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Alert,
  Form,
  Spinner,
} from "react-bootstrap";
import { useNavigate } from "react-router-dom";

/**
 * DashboardCustomer
 * - Gets GPS from browser (GPS-only, per your request)
 * - Posts customer coords to backend endpoint /api/request/update-location
 * - Fetches suppliers from /api/customer/dashboard
 * - Computes haversine distance, sorts suppliers by distance asc
 * - Shows search/filter by name/location
 */

export default function DashboardCustomer() {
  const [suppliers, setSuppliers] = useState([]);
  const [message, setMessage] = useState("");
  const [darkMode, setDarkMode] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [customerLocation, setCustomerLocation] = useState(undefined); // undefined = waiting, null = denied/unavailable
  const [loadingSuppliers, setLoadingSuppliers] = useState(true);

  const navigate = useNavigate();

  // Haversine: returns km as Number
  const haversineKm = useCallback((lat1, lon1, lat2, lon2) => {
    lat1 = Number(lat1);
    lon1 = Number(lon1);
    lat2 = Number(lat2);
    lon2 = Number(lon2);
    if (![lat1, lon1, lat2, lon2].every(Number.isFinite)) return NaN;

    const toRad = (deg) => (deg * Math.PI) / 180;
    const R = 6371; // Earth radius km
    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * Math.sin(dLon / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  }, []);

  // 1) Get customer location (GPS ONLY). Provide small timeout fallback.
  useEffect(() => {
    let timer = null;

    if (!("geolocation" in navigator)) {
      setCustomerLocation(null);
      return;
    }

    const success = (pos) => {
      clearTimeout(timer);
      const lat = pos.coords.latitude;
      const lng = pos.coords.longitude;
      console.log("Customer GPS:", lat, lng);
      setCustomerLocation({ lat, lng });
    };

    const fail = (err) => {
      clearTimeout(timer);
      console.warn("Geolocation failed:", err);
      // If user denies or it fails -> set null (we still fetch suppliers unsorted)
      setCustomerLocation(null);
    };

    navigator.geolocation.getCurrentPosition(success, fail, {
      enableHighAccuracy: true,
      maximumAge: 0,
      timeout: 8000,
    });

    // fallback: if after 6s nothing, continue without location
    timer = setTimeout(() => {
      if (customerLocation === undefined) {
        console.warn("Geolocation timeout -> continuing without location");
        setCustomerLocation(null);
      }
    }, 6000);

    return () => clearTimeout(timer);
  }, []); // run once

  // 2) Apply dark mode flag (your existing behavior)
  useEffect(() => {
    const dm = localStorage.getItem("darkMode") === "true";
    setDarkMode(dm);
    document.body.classList.toggle("dark-mode", dm);
  }, []);

  // 3) Fetch suppliers & compute distance when we know customerLocation (or when it's null)
  useEffect(() => {
    if (customerLocation === undefined) return; // still waiting for GPS

    const fetchAndCompute = async () => {
      setLoadingSuppliers(true);
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:5000/api/customer/dashboard", {
          headers: { Authorization: `Bearer ${token}` },
        });

        let list = (res.data.suppliers || []).filter((s) => !!s.isOnline);

        // If we have customer coords -> compute distances (requires supplier coordinates)
        if (
          customerLocation &&
          Number.isFinite(customerLocation.lat) &&
          Number.isFinite(customerLocation.lng)
        ) {
          list = list.map((s) => {
            // Accept coordinates in s.coordinates.lat/lng OR in s.latitude/sLng shape
            let slat = s?.coordinates?.lat ?? s?.latitude ?? s?.lat ?? null;
            let slng = s?.coordinates?.lng ?? s?.longitude ?? s?.lng ?? null;

            // coerce to numbers when possible
            slat = slat != null ? Number(slat) : null;
            slng = slng != null ? Number(slng) : null;

            let dist = Infinity;
            if (Number.isFinite(slat) && Number.isFinite(slng)) {
              dist = haversineKm(customerLocation.lat, customerLocation.lng, slat, slng);
            }

            return { ...s, distance: Number.isFinite(dist) ? dist : Infinity };
          });

          // Sort ascending by distance
          list.sort((a, b) => (a.distance ?? Infinity) - (b.distance ?? Infinity));
        } else {
          // No gps -> set distance Infinity so UI doesn't show
          list = list.map((s) => ({ ...s, distance: Infinity }));
        }

        setSuppliers(list);
      } catch (err) {
        console.error("Error fetching suppliers:", err);
        setMessage("Failed to load suppliers.");
      } finally {
        setLoadingSuppliers(false);
      }
    };

    fetchAndCompute();
  }, [customerLocation, haversineKm]);

  // Search filter
  const filteredSuppliers = suppliers.filter(
    (s) =>
      (s.name || "").toLowerCase().includes(searchQuery.toLowerCase()) ||
      (s.location || "").toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Booking: update customer location to backend (so server can compute distances when needed) then create request
  const handleBooking = async (supplierId) => {
    const customerId = localStorage.getItem("userId");
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("role");

    if (!token || role !== "CUSTOMER") {
      alert("Please log in as a customer first.");
      return;
    }

    const unitsRequested = prompt("Enter number of units to request:");
    if (!unitsRequested) return;

    try {
      // update customer location server-side if available
      if (customerLocation && Number.isFinite(customerLocation.lat) && Number.isFinite(customerLocation.lng)) {
        await axios.post(
          "http://localhost:5000/api/request/update-location",
          { customerId, lat: customerLocation.lat, lng: customerLocation.lng },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      }

      const res = await axios.post(
        "http://localhost:5000/api/request/create",
        { customerId, supplierId, unitsRequested },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert("✅ Request sent! Waiting for supplier approval...");
      localStorage.setItem("requestId", res.data.request._id);
      navigate(`/customer/booking/${res.data.request._id}`);
    } catch (err) {
      console.error("Booking error:", err);
      alert(err.response?.data?.message || "Failed to send request.");
    }
  };

  return (
    <Container className="mt-5">
      <h2 className={darkMode ? "text-light" : "text-dark"}>Customer Dashboard</h2>

      <Form.Control
        type="text"
        placeholder="Search by name or location..."
        className="my-3"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {message && <Alert variant="info">{message}</Alert>}

      {loadingSuppliers ? (
        <div className="text-center my-4">
          <Spinner animation="border" />
        </div>
      ) : (
        <Row>
          {filteredSuppliers.length === 0 && (
            <p className="text-muted text-center mt-3">No suppliers found.</p>
          )}

          {filteredSuppliers.map((s) => (
            <Col key={s._id} md={4} className="mb-4">
              <Card className={darkMode ? "bg-dark text-light border-secondary" : ""}>
                <Card.Body>
                  <Card.Title>{s.name}</Card.Title>
                  <Card.Text>
                    <strong>Location:</strong> {s.location} <br />
                    <strong>Price:</strong> ₹{s.pricePerUnit} <br />
                    <strong>Available Units:</strong> {s.availableUnits} <br />
                    {Number.isFinite(s.distance) && s.distance !== Infinity && (
                      <span>
                        <strong>Distance:</strong> {s.distance.toFixed(2)} km
                      </span>
                    )}
                  </Card.Text>

                  <Button onClick={() => handleBooking(s._id)}>Book</Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}
