import React, { useState, useEffect } from "react";
import { Container, Row, Col, Form, Button, Card } from "react-bootstrap";
import axios from "axios";

function DashboardSupplier() {
  const [stats, setStats] = useState(null);
  const [newPrice, setNewPrice] = useState("");
  const [supplierId, setSupplierId] = useState(""); // Ideally retrieved from login/localStorage

  useEffect(() => {
    // Example: Get supplierId from localStorage after login
    const id = localStorage.getItem("supplierId");
    setSupplierId(id);

    if (id) {
      axios
        .get(`/api/suppliers/stats/${id}`)
        .then((res) => setStats(res.data))
        .catch((err) => console.error("Error fetching stats:", err));
    }
  }, []);

  const handlePriceUpdate = async (e) => {
    e.preventDefault();
    if (!newPrice) return alert("Please enter a valid price.");

    try {
      await axios.put(`/api/suppliers/stats/updatePrice/${supplierId}`, {
        newPrice: parseFloat(newPrice),
      });
      alert("‚úÖ Price updated successfully!");
      setNewPrice("");
    } catch (err) {
      alert("‚ùå Error updating price: " + err.message);
    }
  };

  return (
    <Container className="mt-5">
      <h2 className="text-center mb-4">Supplier Dashboard</h2>
      <p className="text-center text-muted">
        Manage your electricity pricing and view performance statistics.
      </p>

      {stats ? (
        <Row className="mt-4">
          {/* STATS SECTION */}
          <Col md={6}>
            <Card className="mb-4 shadow-sm">
              <Card.Body>
                <Card.Title>üìä Statistics</Card.Title>
                <p><strong>Total Requests:</strong> {stats.totalRequests}</p>
                <p><strong>Total Units Supplied:</strong> {stats.totalUnitsSupplied}</p>
                <p><strong>Total Revenue:</strong> ‚Çπ{stats.totalRevenue}</p>
                <p>
                  <strong>Last Updated:</strong>{" "}
                  {new Date(stats.lastUpdated).toLocaleString()}
                </p>
              </Card.Body>
            </Card>
          </Col>

          {/* PRICE UPDATE SECTION */}
          <Col md={6}>
            <Card className="shadow-sm">
              <Card.Body>
                <Card.Title>üí∞ Update Price Per Unit</Card.Title>
                <Form onSubmit={handlePriceUpdate}>
                  <Form.Group>
                    <Form.Label>Enter New Price (‚Çπ)</Form.Label>
                    <Form.Control
                      type="number"
                      placeholder="Enter new rate per unit"
                      value={newPrice}
                      onChange={(e) => setNewPrice(e.target.value)}
                    />
                  </Form.Group>
                  <Button type="submit" variant="success" className="mt-3">
                    Update Price
                  </Button>
                </Form>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      ) : (
        <p className="text-center mt-4">Loading supplier data...</p>
      )}
    </Container>
  );
}

export default DashboardSupplier;
